mapper.insert:
INSERT INTO EZC_API_REQ_TEMPLATE(	/* API 요청 템플릿 */
	 API_REQ_ID	/* API 요청 ID, VARCHAR2(5) */
	,API_REQ_NUM	/* API 요청 번호, NUMBER(0,22) */
	,REQ_CONTENT	/* 요청 내용, VARCHAR2(4000) */
	,REQ_SAMPLE_DATA	/* 요청 샘플 데이타, VARCHAR2(2000) */
	,CONN_TIME_OUT	/* 접속 타임 아웃, NUMBER(0,22) */
	,READ_TIME_OUT	/* 리드 타임 아웃, NUMBER(0,22) */
	,USE_YN	/* 사용 여부, CHAR(1) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{apiReqId, jdbcType=VARCHAR}
	,#{apiReqNum, jdbcType=DECIMAL}
	,#{reqContent, jdbcType=VARCHAR}
	,#{reqSampleData, jdbcType=VARCHAR}
	,#{connTimeOut, jdbcType=NUMERIC}
	,#{readTimeOut, jdbcType=NUMERIC}
	,#{useYn, jdbcType=CHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_API_REQ_TEMPLATE /* API 요청 템플릿 */
	USING DUAL ON (
		API_REQ_ID = #{apiReqId, jdbcType=VARCHAR}	/* API 요청 ID, VARCHAR2(5) */
		AND API_REQ_NUM = #{apiReqNum, jdbcType=DECIMAL}	/* API 요청 번호, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 REQ_CONTENT = #{reqContent, jdbcType=VARCHAR}	/* 요청 내용, VARCHAR2(4000) */
			,REQ_SAMPLE_DATA = #{reqSampleData, jdbcType=VARCHAR}	/* 요청 샘플 데이타, VARCHAR2(2000) */
			,CONN_TIME_OUT = #{connTimeOut, jdbcType=NUMERIC}	/* 접속 타임 아웃, NUMBER(0,22) */
			,READ_TIME_OUT = #{readTimeOut, jdbcType=NUMERIC}	/* 리드 타임 아웃, NUMBER(0,22) */
			,USE_YN = #{useYn, jdbcType=CHAR}	/* 사용 여부, CHAR(1) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 API_REQ_ID	/* API 요청 ID, VARCHAR2(5) */
			,API_REQ_NUM	/* API 요청 번호, NUMBER(0,22) */
			,REQ_CONTENT	/* 요청 내용, VARCHAR2(4000) */
			,REQ_SAMPLE_DATA	/* 요청 샘플 데이타, VARCHAR2(2000) */
			,CONN_TIME_OUT	/* 접속 타임 아웃, NUMBER(0,22) */
			,READ_TIME_OUT	/* 리드 타임 아웃, NUMBER(0,22) */
			,USE_YN	/* 사용 여부, CHAR(1) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{apiReqId, jdbcType=VARCHAR}
			,#{apiReqNum, jdbcType=DECIMAL}
			,#{reqContent, jdbcType=VARCHAR}
			,#{reqSampleData, jdbcType=VARCHAR}
			,#{connTimeOut, jdbcType=NUMERIC}
			,#{readTimeOut, jdbcType=NUMERIC}
			,#{useYn, jdbcType=CHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
