mapper.insert:
INSERT INTO EZC_AUTH_GRP(	/* 권한 그룹 */
	 AUTH_GRP_CD	/* 권한 그룹 코드, VARCHAR2(10) */
	,SYS_TYPE	/* 시스템 유형, VARCHAR2(8) */
	,AUTH_GRP_NM	/* 권한 그룹 명, VARCHAR2(50) */
	,USE_YN	/* 사용 여부, CHAR(1) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{authGrpCd, jdbcType=VARCHAR}
	,#{sysType, jdbcType=VARCHAR}
	,#{authGrpNm, jdbcType=VARCHAR}
	,#{useYn, jdbcType=CHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS ROW__NUM, RECORDS.* FROM ( /* #### SQL Body [[ ################# */ SELECT EZC_AUTH_GRP.AUTH_GRP_CD AS authGrpCd /* 권한 그룹 코드, VARCHAR2(10) */ ,EZC_AUTH_GRP.SYS_TYPE AS sysType /* 시스템 유형, VARCHAR2(8) */ ,EZC_AUTH_GRP.AUTH_GRP_NM AS authGrpNm /* 권한 그룹 명, VARCHAR2(50) */ ,EZC_AUTH_GRP.USE_YN AS useYn /* 사용 여부, CHAR(1) */ ,EZC_AUTH_GRP.REG_ID AS regId /* 등록자 ID, VARCHAR2(20) */ ,EZC_AUTH_GRP.REG_DT AS regDt /* 등록 일시, VARCHAR2(14) */ ,EZC_AUTH_GRP.MODI_ID AS modiId /* 수정자 ID, VARCHAR2(20) */ ,EZC_AUTH_GRP.MODI_DT AS modiDt /* 수정 일시, VARCHAR2(14) */ FROM EZC_AUTH_GRP /* 권한 그룹 */ WHERE 1=1
AND EZC_AUTH_GRP.AUTH_GRP_CD = #{param.authGrpCd, jdbcType=VARCHAR} /* 권한 그룹 코드, VARCHAR2(10) */
ORDER BY EZC_AUTH_GRP.AUTH_GRP_CD DESC /* #### SQL Body ]] ################# */ ) RECORDS WHERE ROWNUM <= ((#{param.pageNum, jdbcType=NUMERIC}*#{param.pageCount, jdbcType=NUMERIC})+1) ) WHERE ROW__NUM > (#{param.pageNum, jdbcType=NUMERIC}-1)*#{param.pageCount, jdbcType=NUMERIC}

mapper.update:
MERGE INTO EZC_AUTH_GRP /* 권한 그룹 */
	USING DUAL ON (
		AUTH_GRP_CD = #{authGrpCd, jdbcType=VARCHAR}	/* 권한 그룹 코드, VARCHAR2(10) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 SYS_TYPE = #{sysType, jdbcType=VARCHAR}	/* 시스템 유형, VARCHAR2(8) */
			,AUTH_GRP_NM = #{authGrpNm, jdbcType=VARCHAR}	/* 권한 그룹 명, VARCHAR2(50) */
			,USE_YN = #{useYn, jdbcType=CHAR}	/* 사용 여부, CHAR(1) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 AUTH_GRP_CD	/* 권한 그룹 코드, VARCHAR2(10) */
			,SYS_TYPE	/* 시스템 유형, VARCHAR2(8) */
			,AUTH_GRP_NM	/* 권한 그룹 명, VARCHAR2(50) */
			,USE_YN	/* 사용 여부, CHAR(1) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{authGrpCd, jdbcType=VARCHAR}
			,#{sysType, jdbcType=VARCHAR}
			,#{authGrpNm, jdbcType=VARCHAR}
			,#{useYn, jdbcType=CHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
