mapper.insert:
INSERT INTO EZC_AUTH_GRP(	/* 권한 그룹 */
	 AUTH_GRP_CD	/* 권한 그룹 코드, VARCHAR2(10) */
	,SYS_TYPE	/* 시스템 유형, VARCHAR2(8) */
	,AUTH_GRP_NM	/* 권한 그룹 명, VARCHAR2(50) */
	,USE_YN	/* 사용 여부, CHAR(1) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{authGrpCd, jdbcType=VARCHAR}
	,#{sysType, jdbcType=VARCHAR}
	,#{authGrpNm, jdbcType=VARCHAR}
	,#{useYn, jdbcType=CHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_AUTH_GRP /* 권한 그룹 */
	USING DUAL ON (
		AUTH_GRP_CD = #{authGrpCd, jdbcType=VARCHAR}	/* 권한 그룹 코드, VARCHAR2(10) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 SYS_TYPE = #{sysType, jdbcType=VARCHAR}	/* 시스템 유형, VARCHAR2(8) */
			,AUTH_GRP_NM = #{authGrpNm, jdbcType=VARCHAR}	/* 권한 그룹 명, VARCHAR2(50) */
			,USE_YN = #{useYn, jdbcType=CHAR}	/* 사용 여부, CHAR(1) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 AUTH_GRP_CD	/* 권한 그룹 코드, VARCHAR2(10) */
			,SYS_TYPE	/* 시스템 유형, VARCHAR2(8) */
			,AUTH_GRP_NM	/* 권한 그룹 명, VARCHAR2(50) */
			,USE_YN	/* 사용 여부, CHAR(1) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{authGrpCd, jdbcType=VARCHAR}
			,#{sysType, jdbcType=VARCHAR}
			,#{authGrpNm, jdbcType=VARCHAR}
			,#{useYn, jdbcType=CHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
