mapper.insert:
INSERT INTO EZC_AUTH(	/* 권한 정보 */
	 MENU_ID	/* 메뉴 ID, VARCHAR2(6) */
	,AUTH_GRP_CD	/* 권한 그룹 코드, VARCHAR2(10) */
	,AUTH_C	/* 권한 C, CHAR(1) */
	,AUTH_R	/* 권한 R, CHAR(1) */
	,AUTH_U	/* 권한 U, CHAR(1) */
	,AUTH_D	/* 권한 D, CHAR(1) */
	,AUTH_X	/* 권한 X, CHAR(1) */
	,AUTH_P	/* 권한 P, CHAR(1) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{menuId, jdbcType=VARCHAR}
	,#{authGrpCd, jdbcType=VARCHAR}
	,#{authC, jdbcType=CHAR}
	,#{authR, jdbcType=CHAR}
	,#{authU, jdbcType=CHAR}
	,#{authD, jdbcType=CHAR}
	,#{authX, jdbcType=CHAR}
	,#{authP, jdbcType=CHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_AUTH /* 권한 정보 */
	USING DUAL ON (
		MENU_ID = #{menuId, jdbcType=VARCHAR}	/* 메뉴 ID, VARCHAR2(6) */
		AND AUTH_GRP_CD = #{authGrpCd, jdbcType=VARCHAR}	/* 권한 그룹 코드, VARCHAR2(10) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 AUTH_C = #{authC, jdbcType=CHAR}	/* 권한 C, CHAR(1) */
			,AUTH_R = #{authR, jdbcType=CHAR}	/* 권한 R, CHAR(1) */
			,AUTH_U = #{authU, jdbcType=CHAR}	/* 권한 U, CHAR(1) */
			,AUTH_D = #{authD, jdbcType=CHAR}	/* 권한 D, CHAR(1) */
			,AUTH_X = #{authX, jdbcType=CHAR}	/* 권한 X, CHAR(1) */
			,AUTH_P = #{authP, jdbcType=CHAR}	/* 권한 P, CHAR(1) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 MENU_ID	/* 메뉴 ID, VARCHAR2(6) */
			,AUTH_GRP_CD	/* 권한 그룹 코드, VARCHAR2(10) */
			,AUTH_C	/* 권한 C, CHAR(1) */
			,AUTH_R	/* 권한 R, CHAR(1) */
			,AUTH_U	/* 권한 U, CHAR(1) */
			,AUTH_D	/* 권한 D, CHAR(1) */
			,AUTH_X	/* 권한 X, CHAR(1) */
			,AUTH_P	/* 권한 P, CHAR(1) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{menuId, jdbcType=VARCHAR}
			,#{authGrpCd, jdbcType=VARCHAR}
			,#{authC, jdbcType=CHAR}
			,#{authR, jdbcType=CHAR}
			,#{authU, jdbcType=CHAR}
			,#{authD, jdbcType=CHAR}
			,#{authX, jdbcType=CHAR}
			,#{authP, jdbcType=CHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
