mapper.insert:
INSERT INTO EZC_CACHE_DAY_PRICE_LOG(	/* CACHE 당일특가 로그 */
	 DAY_PRICE_LOG_SEQ	/* 당일특가 로그 일련번호, NUMBER(0,22) */
	,EXE_DT	/* 실행 일시, VARCHAR2(14) */
	,CACHE_END_TM	/* CACHE 만료 시간, VARCHAR2(4) */
	,EXE_STATUS	/* 실행 상태, VARCHAR2(8) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{dayPriceLogSeq, jdbcType=DECIMAL}
	,#{exeDt, jdbcType=VARCHAR}
	,#{cacheEndTm, jdbcType=VARCHAR}
	,#{exeStatus, jdbcType=VARCHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS ROW__NUM, RECORDS.* FROM ( /* #### SQL Body [[ ################# */ SELECT EZC_CACHE_DAY_PRICE_LOG.DAY_PRICE_LOG_SEQ AS dayPriceLogSeq /* 당일특가 로그 일련번호, NUMBER(0,22) */ ,EZC_CACHE_DAY_PRICE_LOG.EXE_DT AS exeDt /* 실행 일시, VARCHAR2(14) */ ,EZC_CACHE_DAY_PRICE_LOG.CACHE_END_TM AS cacheEndTm /* CACHE 만료 시간, VARCHAR2(4) */ ,EZC_CACHE_DAY_PRICE_LOG.EXE_STATUS AS exeStatus /* 실행 상태, VARCHAR2(8) */ ,EZC_CACHE_DAY_PRICE_LOG.REG_ID AS regId /* 등록자 ID, VARCHAR2(20) */ ,EZC_CACHE_DAY_PRICE_LOG.REG_DT AS regDt /* 등록 일시, VARCHAR2(14) */ ,EZC_CACHE_DAY_PRICE_LOG.MODI_ID AS modiId /* 수정자 ID, VARCHAR2(20) */ ,EZC_CACHE_DAY_PRICE_LOG.MODI_DT AS modiDt /* 수정 일시, VARCHAR2(14) */ FROM EZC_CACHE_DAY_PRICE_LOG /* CACHE 당일특가 로그 */ WHERE 1=1
AND EZC_CACHE_DAY_PRICE_LOG.DAY_PRICE_LOG_SEQ = #{param.dayPriceLogSeq, jdbcType=DECIMAL} /* 당일특가 로그 일련번호, NUMBER(0,22) */
ORDER BY EZC_CACHE_DAY_PRICE_LOG.DAY_PRICE_LOG_SEQ DESC /* #### SQL Body ]] ################# */ ) RECORDS WHERE ROWNUM <= ((#{param.pageNum, jdbcType=NUMERIC}*#{param.pageCount, jdbcType=NUMERIC})+1) ) WHERE ROW__NUM > (#{param.pageNum, jdbcType=NUMERIC}-1)*#{param.pageCount, jdbcType=NUMERIC}

mapper.update:
MERGE INTO EZC_CACHE_DAY_PRICE_LOG /* CACHE 당일특가 로그 */
	USING DUAL ON (
		DAY_PRICE_LOG_SEQ = #{dayPriceLogSeq, jdbcType=DECIMAL}	/* 당일특가 로그 일련번호, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 EXE_DT = #{exeDt, jdbcType=VARCHAR}	/* 실행 일시, VARCHAR2(14) */
			,CACHE_END_TM = #{cacheEndTm, jdbcType=VARCHAR}	/* CACHE 만료 시간, VARCHAR2(4) */
			,EXE_STATUS = #{exeStatus, jdbcType=VARCHAR}	/* 실행 상태, VARCHAR2(8) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 DAY_PRICE_LOG_SEQ	/* 당일특가 로그 일련번호, NUMBER(0,22) */
			,EXE_DT	/* 실행 일시, VARCHAR2(14) */
			,CACHE_END_TM	/* CACHE 만료 시간, VARCHAR2(4) */
			,EXE_STATUS	/* 실행 상태, VARCHAR2(8) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{dayPriceLogSeq, jdbcType=DECIMAL}
			,#{exeDt, jdbcType=VARCHAR}
			,#{cacheEndTm, jdbcType=VARCHAR}
			,#{exeStatus, jdbcType=VARCHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
