mapper.insert:
INSERT INTO EZC_CACHE_DAY_PRICE(	/* CACHE 당일특가 */
	 DAY_PRICE_DD	/* 당일특가 일자, VARCHAR2(8) */
	,FACL_CD	/* 시설 코드, NUMBER(0,22) */
	,GRP_FACL_CD	/* 그룹 시설 코드, NUMBER(0,22) */
	,DAY_PRICE_LOG_SEQ	/* 당일특가 로그 일련번호, NUMBER(0,22) */
	,DAY_PRICE_MIN_PRICE	/* 당일특가 최저가, NUMBER(0,22) */
	,SALE_END_TM	/* 판매 종료 시분, VARCHAR2(4) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{dayPriceDd, jdbcType=VARCHAR}
	,#{faclCd, jdbcType=DECIMAL}
	,#{grpFaclCd, jdbcType=DECIMAL}
	,#{dayPriceLogSeq, jdbcType=DECIMAL}
	,#{dayPriceMinPrice, jdbcType=NUMERIC}
	,#{saleEndTm, jdbcType=VARCHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_CACHE_DAY_PRICE /* CACHE 당일특가 */
	USING DUAL ON (
		FACL_CD = #{faclCd, jdbcType=DECIMAL}	/* 시설 코드, NUMBER(0,22) */
		AND GRP_FACL_CD = #{grpFaclCd, jdbcType=DECIMAL}	/* 그룹 시설 코드, NUMBER(0,22) */
		AND DAY_PRICE_DD = #{dayPriceDd, jdbcType=VARCHAR}	/* 당일특가 일자, VARCHAR2(8) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 DAY_PRICE_MIN_PRICE = #{dayPriceMinPrice, jdbcType=NUMERIC}	/* 당일특가 최저가, NUMBER(0,22) */
			,DAY_PRICE_NET_PRICE = #{dayPriceNetPrice, jdbcType=NUMERIC}	/* 당일특가 정상가, NUMBER(0,22) */
			,SALE_END_TM = #{saleEndTm, jdbcType=VARCHAR}	/* 판매 종료 시분, VARCHAR2(12) */
	WHEN NOT MATCHED THEN
		INSERT (
			 DAY_PRICE_DD	/* 당일특가 일자, VARCHAR2(8) */
			,FACL_CD	/* 시설 코드, NUMBER(0,22) */
			,GRP_FACL_CD	/* 그룹 시설 코드, NUMBER(0,22) */
			,DAY_PRICE_MIN_PRICE	/* 당일특가 최저가, NUMBER(0,22) */
			,DAY_PRICE_NET_PRICE	/* 당일특가 정상가, NUMBER(0,22) */
			,SALE_END_TM	/* 판매 종료 시분, VARCHAR2(12) */
		) VALUES (
			 #{dayPriceDd, jdbcType=VARCHAR}
			,#{faclCd, jdbcType=DECIMAL}
			,#{grpFaclCd, jdbcType=DECIMAL}
			,#{dayPriceMinPrice, jdbcType=NUMERIC}
			,#{dayPriceNetPrice, jdbcType=NUMERIC}
			,#{saleEndTm, jdbcType=VARCHAR}
		)
