mapper.insert:
INSERT INTO EZC_CACHE_MIN_AMT(	/* CACHE 최저가 정보 */
	 CHECK_IN_DD	/* 체크인 일자, VARCHAR2(8) */
	,CHECK_OUT_DD	/* 체크아웃 일자, VARCHAR2(8) */
	,GRP_FACL_CD	/* 그룹 시설 코드, NUMBER(0,22) */
	,FACL_CD	/* 시설 코드, NUMBER(0,22) */
	,CITY_CD	/* 도시 코드, VARCHAR2(10) */
	,AREA_CD	/* 지역 코드, VARCHAR2(10) */
	,ROOM_MIN_PRICE	/* 객실 최저가, NUMBER(0,22) */
	,SP_ROOM_MIN_PRICE	/* 특가최저가, NUMBER(0,22) */
	,ROOM_NET_PRICE
	,SP_ROOM_NET_PRICE
	,PARTNER_CD
) VALUES (
	 #{checkInDd, jdbcType=VARCHAR}
	,#{checkOutDd, jdbcType=VARCHAR}
	,#{grpFaclCd, jdbcType=DECIMAL}
	,#{faclCd, jdbcType=DECIMAL}
	,#{cityCd, jdbcType=VARCHAR}
	,#{areaCd, jdbcType=VARCHAR}
	,#{roomMinPrice, jdbcType=NUMERIC}
	,#{spRoomMinPrice, jdbcType=NUMERIC}
	,#{roomNetPrice, jdbcType=DECIMAL}
	,#{spRoomNetPrice, jdbcType=DECIMAL}
	,#{partnerCd, jdbcType=DECIMAL}
)

mapper.update:
MERGE INTO EZC_CACHE_MIN_AMT /* CACHE 최저가 정보 */
	USING DUAL ON (
		CHECK_IN_DD = #{checkInDd, jdbcType=VARCHAR}	/* 체크인 일자, VARCHAR2(8) */
		AND CHECK_OUT_DD = #{checkOutDd, jdbcType=VARCHAR}	/* 체크아웃 일자, VARCHAR2(8) */
		AND GRP_FACL_CD = #{grpFaclCd, jdbcType=DECIMAL}	/* 그룹 시설 코드, NUMBER(0,22) */
		AND FACL_CD = #{faclCd, jdbcType=DECIMAL}	/* 시설 코드, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 ROOM_MIN_PRICE = #{roomMinPrice, jdbcType=NUMERIC}	/* 객실 최저가, NUMBER(0,22) */
			,SP_ROOM_MIN_PRICE = #{spRoomMinPrice, jdbcType=NUMERIC}	/* 특가최저가, NUMBER(0,22) */
			,ROOM_NET_PRICE = #{roomNetPrice, jdbcType=DECIMAL}
			,SP_ROOM_NET_PRICE = #{spRoomNetPrice, jdbcType=DECIMAL}
	WHEN NOT MATCHED THEN
		INSERT (
			 CHECK_IN_DD	/* 체크인 일자, VARCHAR2(8) */
			,CHECK_OUT_DD	/* 체크아웃 일자, VARCHAR2(8) */
			,GRP_FACL_CD	/* 그룹 시설 코드, NUMBER(0,22) */
			,FACL_CD	/* 시설 코드, NUMBER(0,22) */
			,ROOM_MIN_PRICE	/* 객실 최저가, NUMBER(0,22) */
			,SP_ROOM_MIN_PRICE	/* 특가최저가, NUMBER(0,22) */
			,ROOM_NET_PRICE
			,SP_ROOM_NET_PRICE
		) VALUES (
			 #{checkInDd, jdbcType=VARCHAR}
			,#{checkOutDd, jdbcType=VARCHAR}
			,#{grpFaclCd, jdbcType=DECIMAL}
			,#{faclCd, jdbcType=DECIMAL}
			,#{roomMinPrice, jdbcType=NUMERIC}
			,#{spRoomMinPrice, jdbcType=NUMERIC}
			,#{roomNetPrice, jdbcType=DECIMAL}
			,#{spRoomNetPrice, jdbcType=DECIMAL}
		)
