mapper.insert:
INSERT INTO EZC_CACHE_SEARCH_LOG(	/* CACHE 검색 로그 */
	 CHECK_IN_DD	/* 체크인 일자, VARCHAR2(8) */
	,CHECK_OUT_DD	/* 체크아웃 일자, VARCHAR2(8) */
	,CITY_CD	/* 도시 코드, VARCHAR2(10) */
	,AREA_CD	/* 지역 코드, VARCHAR2(10) */
	,EXE_DT	/* 실행 일시, VARCHAR2(14) */
	,CACHE_END_TM	/* CACHE 만료 시간, VARCHAR2(4) */
	,EXE_STATUS	/* 실행 상태, VARCHAR2(8) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{checkInDd, jdbcType=VARCHAR}
	,#{checkOutDd, jdbcType=VARCHAR}
	,#{cityCd, jdbcType=VARCHAR}
	,#{areaCd, jdbcType=VARCHAR}
	,#{exeDt, jdbcType=VARCHAR}
	,#{cacheEndTm, jdbcType=VARCHAR}
	,#{exeStatus, jdbcType=VARCHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_CACHE_SEARCH_LOG /* CACHE 검색 로그 */
	USING DUAL ON (
		CHECK_IN_DD = #{checkInDd, jdbcType=VARCHAR}	/* 체크인 일자, VARCHAR2(8) */
		AND CHECK_OUT_DD = #{checkOutDd, jdbcType=VARCHAR}	/* 체크아웃 일자, VARCHAR2(8) */
		AND CITY_CD = #{cityCd, jdbcType=VARCHAR}	/* 도시 코드, VARCHAR2(10) */
		AND AREA_CD = #{areaCd, jdbcType=VARCHAR}	/* 지역 코드, VARCHAR2(10) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 EXE_DT = #{exeDt, jdbcType=VARCHAR}	/* 실행 일시, VARCHAR2(14) */
			,CACHE_END_TM = #{cacheEndTm, jdbcType=VARCHAR}	/* CACHE 만료 시간, VARCHAR2(4) */
			,EXE_STATUS = #{exeStatus, jdbcType=VARCHAR}	/* 실행 상태, VARCHAR2(8) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 CHECK_IN_DD	/* 체크인 일자, VARCHAR2(8) */
			,CHECK_OUT_DD	/* 체크아웃 일자, VARCHAR2(8) */
			,CITY_CD	/* 도시 코드, VARCHAR2(10) */
			,AREA_CD	/* 지역 코드, VARCHAR2(10) */
			,EXE_DT	/* 실행 일시, VARCHAR2(14) */
			,CACHE_END_TM	/* CACHE 만료 시간, VARCHAR2(4) */
			,EXE_STATUS	/* 실행 상태, VARCHAR2(8) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{checkInDd, jdbcType=VARCHAR}
			,#{checkOutDd, jdbcType=VARCHAR}
			,#{cityCd, jdbcType=VARCHAR}
			,#{areaCd, jdbcType=VARCHAR}
			,#{exeDt, jdbcType=VARCHAR}
			,#{cacheEndTm, jdbcType=VARCHAR}
			,#{exeStatus, jdbcType=VARCHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
