) VALUES (
	 #{channelCd, jdbcType=VARCHAR}
	,#{grpFaclYn, jdbcType=CHAR}
	,#{conFaclCd, jdbcType=DECIMAL}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_CHANNEL_BLACK_FACL /* 채널 블랙 시설 */
	USING DUAL ON (
		CHANNEL_CD = #{channelCd, jdbcType=VARCHAR}	/* 채널 코드, VARCHAR2(10) */
		AND GRP_FACL_YN = #{grpFaclYn, jdbcType=CHAR}	/* 그룹 시설 여부, CHAR(1) */
		AND CON_FACL_CD = #{conFaclCd, jdbcType=DECIMAL}	/* 연계 시설 코드, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
	WHEN NOT MATCHED THEN
		INSERT (
			 CHANNEL_CD	/* 채널 코드, VARCHAR2(10) */
			,GRP_FACL_YN	/* 그룹 시설 여부, CHAR(1) */
			,CON_FACL_CD	/* 연계 시설 코드, NUMBER(0,22) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{channelCd, jdbcType=VARCHAR}
			,#{grpFaclYn, jdbcType=CHAR}
			,#{conFaclCd, jdbcType=DECIMAL}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
