mapper.insert:
INSERT INTO EZC_CHANNEL_CITYCD(	/* 채널 도시코드 */
	 CHANNEL_CD	/* 채널 코드, VARCHAR2(10) */
	,CITY_CD	/* 도시 코드, VARCHAR2(10) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{channelCd, jdbcType=VARCHAR}
	,#{cityCd, jdbcType=VARCHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_CHANNEL_CITYCD /* 채널 도시코드 */
	USING DUAL ON (
		CHANNEL_CD = #{channelCd, jdbcType=VARCHAR}	/* 채널 코드, VARCHAR2(10) */
		AND CITY_CD = #{cityCd, jdbcType=VARCHAR}	/* 도시 코드, VARCHAR2(10) */
	)
	WHEN MATCHED THEN
		UPDATE SET
	WHEN NOT MATCHED THEN
		INSERT (
			 CHANNEL_CD	/* 채널 코드, VARCHAR2(10) */
			,CITY_CD	/* 도시 코드, VARCHAR2(10) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{channelCd, jdbcType=VARCHAR}
			,#{cityCd, jdbcType=VARCHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
