mapper.insert:
INSERT INTO EZC_CHANNEL_CLIENT_PARTNER(	/* 채널 고객사 제휴사 */
	 CLIENT_CD	/* 고객사 코드, VARCHAR2(20) */
	,PARTNER_CD_TYPE	/* 제휴사 코드 유형, VARCHAR2(8) */
	,CHANNEL_CD	/* 채널 코드, VARCHAR2(10) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{clientCd, jdbcType=VARCHAR}
	,#{partnerCdType, jdbcType=VARCHAR}
	,#{channelCd, jdbcType=VARCHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_CHANNEL_CLIENT_PARTNER /* 채널 고객사 제휴사 */
	USING DUAL ON (
		CLIENT_CD = #{clientCd, jdbcType=VARCHAR}	/* 고객사 코드, VARCHAR2(20) */
		AND PARTNER_CD_TYPE = #{partnerCdType, jdbcType=VARCHAR}	/* 제휴사 코드 유형, VARCHAR2(8) */
		AND CHANNEL_CD = #{channelCd, jdbcType=VARCHAR}	/* 채널 코드, VARCHAR2(10) */
	)
	WHEN MATCHED THEN
		UPDATE SET
	WHEN NOT MATCHED THEN
		INSERT (
			 CLIENT_CD	/* 고객사 코드, VARCHAR2(20) */
			,PARTNER_CD_TYPE	/* 제휴사 코드 유형, VARCHAR2(8) */
			,CHANNEL_CD	/* 채널 코드, VARCHAR2(10) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{clientCd, jdbcType=VARCHAR}
			,#{partnerCdType, jdbcType=VARCHAR}
			,#{channelCd, jdbcType=VARCHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
