mapper.insert:
INSERT INTO EZC_CHANNEL_MNG(	/* 채널 */
	 CHANNEL_CD	/* 채널 코드, VARCHAR2(10) */
	,CHANNEL_CD_NM	/* 채널 코드 명, VARCHAR2(50) */
	,ROOM_SET_YN	/* 숙소 설정 여부, CHAR(1) */
	,PARTNER_SET_YN	/* 제휴사 설정 여부, CHAR(1) */
	,FACL_SET_YN	/* 시설 설정 여부, CHAR(1) */
	,MARK_TYPE	/* 마크 유형, VARCHAR2(8) */
	,MARK_UNIT	/* 마크 단위, VARCHAR2(8) */
	,MARK_AMT	/* 마크 금액, NUMBER(0,22) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{channelCd, jdbcType=VARCHAR}
	,#{channelCdNm, jdbcType=VARCHAR}
	,#{roomSetYn, jdbcType=CHAR}
	,#{partnerSetYn, jdbcType=CHAR}
	,#{faclSetYn, jdbcType=CHAR}
	,#{markType, jdbcType=VARCHAR}
	,#{markUnit, jdbcType=VARCHAR}
	,#{markAmt, jdbcType=NUMERIC}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_CHANNEL_MNG /* 채널 */
	USING DUAL ON (
		CHANNEL_CD = #{channelCd, jdbcType=VARCHAR}	/* 채널 코드, VARCHAR2(10) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 CHANNEL_CD_NM = #{channelCdNm, jdbcType=VARCHAR}	/* 채널 코드 명, VARCHAR2(50) */
			,ROOM_SET_YN = #{roomSetYn, jdbcType=CHAR}	/* 숙소 설정 여부, CHAR(1) */
			,PARTNER_SET_YN = #{partnerSetYn, jdbcType=CHAR}	/* 제휴사 설정 여부, CHAR(1) */
			,FACL_SET_YN = #{faclSetYn, jdbcType=CHAR}	/* 시설 설정 여부, CHAR(1) */
			,MARK_TYPE = #{markType, jdbcType=VARCHAR}	/* 마크 유형, VARCHAR2(8) */
			,MARK_UNIT = #{markUnit, jdbcType=VARCHAR}	/* 마크 단위, VARCHAR2(8) */
			,MARK_AMT = #{markAmt, jdbcType=NUMERIC}	/* 마크 금액, NUMBER(0,22) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 CHANNEL_CD	/* 채널 코드, VARCHAR2(10) */
			,CHANNEL_CD_NM	/* 채널 코드 명, VARCHAR2(50) */
			,ROOM_SET_YN	/* 숙소 설정 여부, CHAR(1) */
			,PARTNER_SET_YN	/* 제휴사 설정 여부, CHAR(1) */
			,FACL_SET_YN	/* 시설 설정 여부, CHAR(1) */
			,MARK_TYPE	/* 마크 유형, VARCHAR2(8) */
			,MARK_UNIT	/* 마크 단위, VARCHAR2(8) */
			,MARK_AMT	/* 마크 금액, NUMBER(0,22) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{channelCd, jdbcType=VARCHAR}
			,#{channelCdNm, jdbcType=VARCHAR}
			,#{roomSetYn, jdbcType=CHAR}
			,#{partnerSetYn, jdbcType=CHAR}
			,#{faclSetYn, jdbcType=CHAR}
			,#{markType, jdbcType=VARCHAR}
			,#{markUnit, jdbcType=VARCHAR}
			,#{markAmt, jdbcType=NUMERIC}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
