mapper.select:
SELECT * FROM ( SELECT ROWNUM AS ROW__NUM, RECORDS.* FROM ( /* #### SQL Body [[ ################# */ SELECT EZC_CHANNEL_MNG.CHANNEL_CD AS channelCd /* 채널 코드, VARCHAR2(10) */ ,EZC_CHANNEL_MNG.CHANNEL_CD_NM AS channelCdNm /* 채널 코드 명, VARCHAR2(50) */ ,EZC_CHANNEL_MNG.ROOM_SET_YN AS roomSetYn /* 숙소 설정 여부, CHAR(1) */ ,EZC_CHANNEL_MNG.PARTNER_SET_YN AS partnerSetYn /* 제휴사 설정 여부, CHAR(1) */ ,EZC_CHANNEL_MNG.FACL_SET_YN AS faclSetYn /* 시설 설정 여부, CHAR(1) */ ,EZC_CHANNEL_MNG.MARK_TYPE AS markType /* 마크 유형, VARCHAR2(8) */ ,EZC_CHANNEL_MNG.MARK_UNIT AS markUnit /* 마크 단위, VARCHAR2(8) */ ,EZC_CHANNEL_MNG.MARK_AMT AS markAmt /* 마크 금액, NUMBER(0,22) */ ,EZC_CHANNEL_MNG.REG_ID AS regId /* 등록자 ID, VARCHAR2(20) */ ,EZC_CHANNEL_MNG.REG_DT AS regDt /* 등록 일시, VARCHAR2(14) */ ,EZC_CHANNEL_MNG.MODI_ID AS modiId /* 수정자 ID, VARCHAR2(20) */ ,EZC_CHANNEL_MNG.MODI_DT AS modiDt /* 수정 일시, VARCHAR2(14) */ FROM EZC_CHANNEL_MNG /* 채널 */ WHERE 1=1
AND EZC_CHANNEL_MNG.CHANNEL_CD = #{param.channelCd, jdbcType=VARCHAR} /* 채널 코드, VARCHAR2(10) */
ORDER BY EZC_CHANNEL_MNG.CHANNEL_CD DESC /* #### SQL Body ]] ################# */ ) RECORDS WHERE ROWNUM <= ((#{param.pageNum, jdbcType=NUMERIC}*#{param.pageCount, jdbcType=NUMERIC})+1) ) WHERE ROW__NUM > (#{param.pageNum, jdbcType=NUMERIC}-1)*#{param.pageCount, jdbcType=NUMERIC}

mapper.update:
MERGE INTO EZC_CHANNEL_MNG /* 채널 */
	USING DUAL ON (
		CHANNEL_CD = #{channelCd, jdbcType=VARCHAR}	/* 채널 코드, VARCHAR2(10) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 CHANNEL_CD_NM = #{channelCdNm, jdbcType=VARCHAR}	/* 채널 코드 명, VARCHAR2(50) */
			,ROOM_SET_YN = #{roomSetYn, jdbcType=CHAR}	/* 숙소 설정 여부, CHAR(1) */
			,PARTNER_SET_YN = #{partnerSetYn, jdbcType=CHAR}	/* 제휴사 설정 여부, CHAR(1) */
			,FACL_SET_YN = #{faclSetYn, jdbcType=CHAR}	/* 시설 설정 여부, CHAR(1) */
			,MARK_TYPE = #{markType, jdbcType=VARCHAR}	/* 마크 유형, VARCHAR2(8) */
			,MARK_UNIT = #{markUnit, jdbcType=VARCHAR}	/* 마크 단위, VARCHAR2(8) */
			,MARK_AMT = #{markAmt, jdbcType=NUMERIC}	/* 마크 금액, NUMBER(0,22) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 CHANNEL_CD	/* 채널 코드, VARCHAR2(10) */
			,CHANNEL_CD_NM	/* 채널 코드 명, VARCHAR2(50) */
			,ROOM_SET_YN	/* 숙소 설정 여부, CHAR(1) */
			,PARTNER_SET_YN	/* 제휴사 설정 여부, CHAR(1) */
			,FACL_SET_YN	/* 시설 설정 여부, CHAR(1) */
			,MARK_TYPE	/* 마크 유형, VARCHAR2(8) */
			,MARK_UNIT	/* 마크 단위, VARCHAR2(8) */
			,MARK_AMT	/* 마크 금액, NUMBER(0,22) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{channelCd, jdbcType=VARCHAR}
			,#{channelCdNm, jdbcType=VARCHAR}
			,#{roomSetYn, jdbcType=CHAR}
			,#{partnerSetYn, jdbcType=CHAR}
			,#{faclSetYn, jdbcType=CHAR}
			,#{markType, jdbcType=VARCHAR}
			,#{markUnit, jdbcType=VARCHAR}
			,#{markAmt, jdbcType=NUMERIC}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
