mapper.insert:
INSERT INTO EZC_CHANNEL_ROOM_TYPE(	/* 채널 숙소 유형 */
	 CHANNEL_CD	/* 채널 코드, VARCHAR2(10) */
	,ROOM_TYPE	/* 숙소 유형, VARCHAR2(8) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{channelCd, jdbcType=VARCHAR}
	,#{roomType, jdbcType=VARCHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_CHANNEL_ROOM_TYPE /* 채널 숙소 유형 */
	USING DUAL ON (
		CHANNEL_CD = #{channelCd, jdbcType=VARCHAR}	/* 채널 코드, VARCHAR2(10) */
		AND ROOM_TYPE = #{roomType, jdbcType=VARCHAR}	/* 숙소 유형, VARCHAR2(8) */
	)
	WHEN MATCHED THEN
		UPDATE SET
	WHEN NOT MATCHED THEN
		INSERT (
			 CHANNEL_CD	/* 채널 코드, VARCHAR2(10) */
			,ROOM_TYPE	/* 숙소 유형, VARCHAR2(8) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{channelCd, jdbcType=VARCHAR}
			,#{roomType, jdbcType=VARCHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
