mapper.insert:
INSERT INTO EZC_CITY_CD(	/* 도시 코드 */
	 CITY_CD	/* 도시 코드, VARCHAR2(10) */
	,CITY_NM	/* 도시 명, VARCHAR2(50) */
	,COORD_Y	/* 위도, VARCHAR2(20) */
	,COORD_X	/* 경도, VARCHAR2(20) */
	,DISP_ORDER	/* 정렬순서, NUMBER(0,22) */
	,USE_YN	/* 사용 여부, CHAR(1) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{cityCd, jdbcType=VARCHAR}
	,#{cityNm, jdbcType=VARCHAR}
	,#{coordY, jdbcType=VARCHAR}
	,#{coordX, jdbcType=VARCHAR}
	,#{dispOrder, jdbcType=NUMERIC}
	,#{useYn, jdbcType=CHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS ROW__NUM, RECORDS.* FROM ( /* #### SQL Body [[ ################# */ SELECT EZC_CITY_CD.CITY_CD AS cityCd /* 도시 코드, VARCHAR2(10) */ ,EZC_CITY_CD.CITY_NM AS cityNm /* 도시 명, VARCHAR2(50) */ ,EZC_CITY_CD.COORD_Y AS coordY /* 위도, VARCHAR2(20) */ ,EZC_CITY_CD.COORD_X AS coordX /* 경도, VARCHAR2(20) */ ,EZC_CITY_CD.DISP_ORDER AS dispOrder /* 정렬순서, NUMBER(0,22) */ ,EZC_CITY_CD.USE_YN AS useYn /* 사용 여부, CHAR(1) */ ,EZC_CITY_CD.REG_ID AS regId /* 등록자 ID, VARCHAR2(20) */ ,EZC_CITY_CD.REG_DT AS regDt /* 등록 일시, VARCHAR2(14) */ ,EZC_CITY_CD.MODI_ID AS modiId /* 수정자 ID, VARCHAR2(20) */ ,EZC_CITY_CD.MODI_DT AS modiDt /* 수정 일시, VARCHAR2(14) */ FROM EZC_CITY_CD /* 도시 코드 */ WHERE 1=1
AND EZC_CITY_CD.CITY_CD = #{param.cityCd, jdbcType=VARCHAR} /* 도시 코드, VARCHAR2(10) */
ORDER BY EZC_CITY_CD.CITY_CD DESC /* #### SQL Body ]] ################# */ ) RECORDS WHERE ROWNUM <= ((#{param.pageNum, jdbcType=NUMERIC}*#{param.pageCount, jdbcType=NUMERIC})+1) ) WHERE ROW__NUM > (#{param.pageNum, jdbcType=NUMERIC}-1)*#{param.pageCount, jdbcType=NUMERIC}

mapper.update:
MERGE INTO EZC_CITY_CD /* 도시 코드 */
	USING DUAL ON (
		CITY_CD = #{cityCd, jdbcType=VARCHAR}	/* 도시 코드, VARCHAR2(10) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 CITY_NM = #{cityNm, jdbcType=VARCHAR}	/* 도시 명, VARCHAR2(50) */
			,COORD_Y = #{coordY, jdbcType=VARCHAR}	/* 위도, VARCHAR2(20) */
			,COORD_X = #{coordX, jdbcType=VARCHAR}	/* 경도, VARCHAR2(20) */
			,DISP_ORDER = #{dispOrder, jdbcType=NUMERIC}	/* 정렬순서, NUMBER(0,22) */
			,USE_YN = #{useYn, jdbcType=CHAR}	/* 사용 여부, CHAR(1) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 CITY_CD	/* 도시 코드, VARCHAR2(10) */
			,CITY_NM	/* 도시 명, VARCHAR2(50) */
			,COORD_Y	/* 위도, VARCHAR2(20) */
			,COORD_X	/* 경도, VARCHAR2(20) */
			,DISP_ORDER	/* 정렬순서, NUMBER(0,22) */
			,USE_YN	/* 사용 여부, CHAR(1) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{cityCd, jdbcType=VARCHAR}
			,#{cityNm, jdbcType=VARCHAR}
			,#{coordY, jdbcType=VARCHAR}
			,#{coordX, jdbcType=VARCHAR}
			,#{dispOrder, jdbcType=NUMERIC}
			,#{useYn, jdbcType=CHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
