mapper.insert:
INSERT INTO EZC_CLIENT_INFO(	/* 고객사 */
	 CLIENT_CD	/* 고객사 코드, VARCHAR2(20) */
	,CLIENT_NM	/* 고객사 명, VARCHAR2(100) */
	,MARK_TYPE	/* 마크 유형, VARCHAR2(8) */
	,MARK_UNIT	/* 마크 단위, VARCHAR2(8) */
	,MARK_AMT	/* 마크 금액, NUMBER(0,22) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{clientCd, jdbcType=VARCHAR}
	,#{clientNm, jdbcType=VARCHAR}
	,#{markType, jdbcType=VARCHAR}
	,#{markUnit, jdbcType=VARCHAR}
	,#{markAmt, jdbcType=NUMERIC}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS ROW__NUM, RECORDS.* FROM ( /* #### SQL Body [[ ################# */ SELECT EZC_CLIENT_INFO.CLIENT_CD AS clientCd /* 고객사 코드, VARCHAR2(20) */ ,EZC_CLIENT_INFO.CLIENT_NM AS clientNm /* 고객사 명, VARCHAR2(100) */ ,EZC_CLIENT_INFO.MARK_TYPE AS markType /* 마크 유형, VARCHAR2(8) */ ,EZC_CLIENT_INFO.MARK_UNIT AS markUnit /* 마크 단위, VARCHAR2(8) */ ,EZC_CLIENT_INFO.MARK_AMT AS markAmt /* 마크 금액, NUMBER(0,22) */ ,EZC_CLIENT_INFO.REG_ID AS regId /* 등록자 ID, VARCHAR2(20) */ ,EZC_CLIENT_INFO.REG_DT AS regDt /* 등록 일시, VARCHAR2(14) */ ,EZC_CLIENT_INFO.MODI_ID AS modiId /* 수정자 ID, VARCHAR2(20) */ ,EZC_CLIENT_INFO.MODI_DT AS modiDt /* 수정 일시, VARCHAR2(14) */ FROM EZC_CLIENT_INFO /* 고객사 */ WHERE 1=1
AND EZC_CLIENT_INFO.CLIENT_CD = #{param.clientCd, jdbcType=VARCHAR} /* 고객사 코드, VARCHAR2(20) */
ORDER BY EZC_CLIENT_INFO.CLIENT_CD DESC /* #### SQL Body ]] ################# */ ) RECORDS WHERE ROWNUM <= ((#{param.pageNum, jdbcType=NUMERIC}*#{param.pageCount, jdbcType=NUMERIC})+1) ) WHERE ROW__NUM > (#{param.pageNum, jdbcType=NUMERIC}-1)*#{param.pageCount, jdbcType=NUMERIC}

mapper.update:
MERGE INTO EZC_CLIENT_INFO /* 고객사 */
	USING DUAL ON (
		CLIENT_CD = #{clientCd, jdbcType=VARCHAR}	/* 고객사 코드, VARCHAR2(20) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 CLIENT_NM = #{clientNm, jdbcType=VARCHAR}	/* 고객사 명, VARCHAR2(100) */
			,MARK_TYPE = #{markType, jdbcType=VARCHAR}	/* 마크 유형, VARCHAR2(8) */
			,MARK_UNIT = #{markUnit, jdbcType=VARCHAR}	/* 마크 단위, VARCHAR2(8) */
			,MARK_AMT = #{markAmt, jdbcType=NUMERIC}	/* 마크 금액, NUMBER(0,22) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 CLIENT_CD	/* 고객사 코드, VARCHAR2(20) */
			,CLIENT_NM	/* 고객사 명, VARCHAR2(100) */
			,MARK_TYPE	/* 마크 유형, VARCHAR2(8) */
			,MARK_UNIT	/* 마크 단위, VARCHAR2(8) */
			,MARK_AMT	/* 마크 금액, NUMBER(0,22) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{clientCd, jdbcType=VARCHAR}
			,#{clientNm, jdbcType=VARCHAR}
			,#{markType, jdbcType=VARCHAR}
			,#{markUnit, jdbcType=VARCHAR}
			,#{markAmt, jdbcType=NUMERIC}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
