	USING DUAL ON (
		GRP_FACL_YN = #{grpFaclYn, jdbcType=CHAR}	/* 그룹 시설 여부, CHAR(1) */
		AND CON_FACL_CD = #{conFaclCd, jdbcType=DECIMAL}	/* 연계 시설 코드, NUMBER(0,22) */
		AND CLIENT_CD = #{clientCd, jdbcType=VARCHAR}	/* 고객사 코드, VARCHAR2(20) */
		AND CHANNEL_CD = #{channelCd, jdbcType=VARCHAR}	/* 채널 코드, VARCHAR2(10) */
	)
	WHEN MATCHED THEN
		UPDATE SET
	WHEN NOT MATCHED THEN
		INSERT (
			 GRP_FACL_YN	/* 그룹 시설 여부, CHAR(1) */
			,CON_FACL_CD	/* 연계 시설 코드, NUMBER(0,22) */
