mapper.insert:
INSERT INTO EZC_CPN_CITY(	/* 쿠폰 도시 */
	 CPN_CD	/* 쿠폰 코드, NUMBER(0,22) */
	,CITY_CD	/* 도시 코드, VARCHAR2(10) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{cpnCd, jdbcType=DECIMAL}
	,#{cityCd, jdbcType=VARCHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_CPN_CITY /* 쿠폰 도시 */
	USING DUAL ON (
		CPN_CD = #{cpnCd, jdbcType=DECIMAL}	/* 쿠폰 코드, NUMBER(0,22) */
		AND CITY_CD = #{cityCd, jdbcType=VARCHAR}	/* 도시 코드, VARCHAR2(10) */
	)
	WHEN MATCHED THEN
		UPDATE SET
	WHEN NOT MATCHED THEN
		INSERT (
			 CPN_CD	/* 쿠폰 코드, NUMBER(0,22) */
			,CITY_CD	/* 도시 코드, VARCHAR2(10) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{cpnCd, jdbcType=DECIMAL}
			,#{cityCd, jdbcType=VARCHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
