mapper.insert:
INSERT INTO EZC_CPN_FACL(	/* 쿠폰 시설 */
	 CPN_CD	/* 쿠폰 코드, NUMBER(0,22) */
	,GRP_FACL_CD	/* 그룹 시설 코드, NUMBER(0,22) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{cpnCd, jdbcType=DECIMAL}
	,#{grpFaclCd, jdbcType=DECIMAL}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_CPN_FACL /* 쿠폰 시설 */
	USING DUAL ON (
		CPN_CD = #{cpnCd, jdbcType=DECIMAL}	/* 쿠폰 코드, NUMBER(0,22) */
		AND GRP_FACL_CD = #{grpFaclCd, jdbcType=DECIMAL}	/* 그룹 시설 코드, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
	WHEN NOT MATCHED THEN
		INSERT (
			 CPN_CD	/* 쿠폰 코드, NUMBER(0,22) */
			,GRP_FACL_CD	/* 그룹 시설 코드, NUMBER(0,22) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{cpnCd, jdbcType=DECIMAL}
			,#{grpFaclCd, jdbcType=DECIMAL}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
