mapper.select:
SELECT * FROM ( SELECT ROWNUM AS ROW__NUM, RECORDS.* FROM ( /* #### SQL Body [[ ################# */ SELECT EZC_CPN.CPN_CD AS cpnCd /* 쿠폰 코드, NUMBER(0,22) */ ,EZC_CPN.CPN_NM AS cpnNm /* 쿠폰 명, VARCHAR2(100) */ ,EZC_CPN.DEVICE_DIV AS deviceDiv /* 디바이스 구분, VARCHAR2(8) */ ,EZC_CPN.DC_TYPE AS dcType /* 할인 유형, VARCHAR2(8) */ ,EZC_CPN.DC_VALUE AS dcValue /* 할인 값, NUMBER(0,22) */ ,EZC_CPN.ISSUE_START_DD AS issueStartDd /* 발행 시작 일자, VARCHAR2(8) */ ,EZC_CPN.ISSUE_END_DD AS issueEndDd /* 발행 만료 일자, VARCHAR2(8) */ ,EZC_CPN.ISSUE_START_TM AS issueStartTm /* 발행 시작 시간, VARCHAR2(4) */ ,EZC_CPN.ISSUE_END_TM AS issueEndTm /* 발행 만료 시간, VARCHAR2(4) */ ,EZC_CPN.PURCHASE_LIMIT AS purchaseLimit /* 매수 제한, NUMBER(0,22) */ ,EZC_CPN.MIN_APPLY_AMT AS minApplyAmt /* 최소 적용 금액, NUMBER(0,22) */ ,EZC_CPN.CPN_IMG_PATH AS cpnImgPath /* 쿠폰 이미지 경로, VARCHAR2(100) */ ,EZC_CPN.CPN_IMG_FILENM AS cpnImgFilenm /* 쿠폰 이미지 파일명, VARCHAR2(100) */ ,EZC_CPN.ISSUE_DT AS issueDt /* 발행 일시, VARCHAR2(14) */ ,EZC_CPN.ISSUE_QTY AS issueQty /* 발생 수량, NUMBER(0,22) */ ,EZC_CPN.REG_ID AS regId /* 등록자 ID, VARCHAR2(20) */ ,EZC_CPN.REG_DT AS regDt /* 등록 일시, VARCHAR2(14) */ ,EZC_CPN.MODI_ID AS modiId /* 수정자 ID, VARCHAR2(20) */ ,EZC_CPN.MODI_DT AS modiDt /* 수정 일시, VARCHAR2(14) */ FROM EZC_CPN /* 쿠폰 */ WHERE 1=1
AND EZC_CPN.CPN_CD = #{param.cpnCd, jdbcType=DECIMAL} /* 쿠폰 코드, NUMBER(0,22) */
ORDER BY EZC_CPN.CPN_CD DESC /* #### SQL Body ]] ################# */ ) RECORDS WHERE ROWNUM <= ((#{param.pageNum, jdbcType=NUMERIC}*#{param.pageCount, jdbcType=NUMERIC})+1) ) WHERE ROW__NUM > (#{param.pageNum, jdbcType=NUMERIC}-1)*#{param.pageCount, jdbcType=NUMERIC}

mapper.update:
MERGE INTO EZC_CPN /* 쿠폰 */
	USING DUAL ON (
		CPN_CD = #{cpnCd, jdbcType=DECIMAL}	/* 쿠폰 코드, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 CPN_NM = #{cpnNm, jdbcType=VARCHAR}	/* 쿠폰 명, VARCHAR2(100) */
