mapper.insert:
INSERT INTO EZC_CPN(	/* 쿠폰 */
	 CPN_CD	/* 쿠폰 코드, NUMBER(0,22) */
	,CPN_NM	/* 쿠폰 명, VARCHAR2(100) */
	,DEVICE_DIV	/* 디바이스 구분, VARCHAR2(8) */
	,DC_TYPE	/* 할인 유형, VARCHAR2(8) */
	,DC_VALUE	/* 할인 값, NUMBER(0,22) */
	,ISSUE_START_DD	/* 발행 시작 일자, VARCHAR2(8) */
	,ISSUE_END_DD	/* 발행 만료 일자, VARCHAR2(8) */
	,ISSUE_START_TM	/* 발행 시작 시간, VARCHAR2(4) */
	,ISSUE_END_TM	/* 발행 만료 시간, VARCHAR2(4) */
	,PURCHASE_LIMIT	/* 매수 제한, NUMBER(0,22) */
	,MIN_APPLY_AMT	/* 최소 적용 금액, NUMBER(0,22) */
	,CPN_IMG_PATH	/* 쿠폰 이미지 경로, VARCHAR2(100) */
	,CPN_IMG_FILENM	/* 쿠폰 이미지 파일명, VARCHAR2(100) */
	,ISSUE_DT	/* 발행 일시, VARCHAR2(14) */
	,ISSUE_QTY	/* 발생 수량, NUMBER(0,22) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{cpnCd, jdbcType=DECIMAL}
	,#{cpnNm, jdbcType=VARCHAR}
	,#{deviceDiv, jdbcType=VARCHAR}
	,#{dcType, jdbcType=VARCHAR}
	,#{dcValue, jdbcType=NUMERIC}
	,#{issueStartDd, jdbcType=VARCHAR}
	,#{issueEndDd, jdbcType=VARCHAR}
	,#{issueStartTm, jdbcType=VARCHAR}
	,#{issueEndTm, jdbcType=VARCHAR}
	,#{purchaseLimit, jdbcType=NUMERIC}
	,#{minApplyAmt, jdbcType=NUMERIC}
