	,#{cpnImgFilenm, jdbcType=VARCHAR}
	,#{issueDt, jdbcType=VARCHAR}
	,#{issueQty, jdbcType=NUMERIC}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_CPN /* 쿠폰 */
	USING DUAL ON (
		CPN_CD = #{cpnCd, jdbcType=DECIMAL}	/* 쿠폰 코드, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 CPN_NM = #{cpnNm, jdbcType=VARCHAR}	/* 쿠폰 명, VARCHAR2(100) */
