			,ISSUE_END_DD	/* 발행 만료 일자, VARCHAR2(8) */
			,ISSUE_START_TM	/* 발행 시작 시간, VARCHAR2(4) */
			,ISSUE_END_TM	/* 발행 만료 시간, VARCHAR2(4) */
			,PURCHASE_LIMIT	/* 매수 제한, NUMBER(0,22) */
			,MIN_APPLY_AMT	/* 최소 적용 금액, NUMBER(0,22) */
			,CPN_IMG_PATH	/* 쿠폰 이미지 경로, VARCHAR2(100) */
