mapper.insert:
INSERT INTO EZC_CPN_PARTNER(	/* 쿠폰 제휴사 */
	 CPN_CD	/* 쿠폰 코드, NUMBER(0,22) */
	,PARTNER_CD_TYPE	/* 제휴사 코드 유형, VARCHAR2(8) */
	,CAL_RATIO	/* 정산 비율, NUMBER(0,22) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{cpnCd, jdbcType=DECIMAL}
	,#{partnerCdType, jdbcType=VARCHAR}
	,#{calRatio, jdbcType=NUMERIC}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_CPN_PARTNER /* 쿠폰 제휴사 */
	USING DUAL ON (
		CPN_CD = #{cpnCd, jdbcType=DECIMAL}	/* 쿠폰 코드, NUMBER(0,22) */
		AND PARTNER_CD_TYPE = #{partnerCdType, jdbcType=VARCHAR}	/* 제휴사 코드 유형, VARCHAR2(8) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 CAL_RATIO = #{calRatio, jdbcType=NUMERIC}	/* 정산 비율, NUMBER(0,22) */
	WHEN NOT MATCHED THEN
		INSERT (
			 CPN_CD	/* 쿠폰 코드, NUMBER(0,22) */
			,PARTNER_CD_TYPE	/* 제휴사 코드 유형, VARCHAR2(8) */
			,CAL_RATIO	/* 정산 비율, NUMBER(0,22) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{cpnCd, jdbcType=DECIMAL}
			,#{partnerCdType, jdbcType=VARCHAR}
			,#{calRatio, jdbcType=NUMERIC}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
