mapper.insert:
INSERT INTO EZC_CPN_USE_HIST(	/* 쿠폰 사용 내역 */
	 CPN_CD	/* 쿠폰 코드, NUMBER(0,22) */
	,USER_ID	/* 회원 ID, VARCHAR2(20) */
	,CHANNEL_CD	/* 채널 코드, VARCHAR2(10) */
	,RESERV_NUM	/* 예약 번호, NUMBER(0,22) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{cpnCd, jdbcType=DECIMAL}
	,#{userId, jdbcType=VARCHAR}
	,#{channelCd, jdbcType=VARCHAR}
	,#{reservNum, jdbcType=DECIMAL}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_CPN_USE_HIST /* 쿠폰 사용 내역 */
	USING DUAL ON (
		CPN_CD = #{cpnCd, jdbcType=DECIMAL}	/* 쿠폰 코드, NUMBER(0,22) */
		AND USER_ID = #{userId, jdbcType=VARCHAR}	/* 회원 ID, VARCHAR2(20) */
		AND CHANNEL_CD = #{channelCd, jdbcType=VARCHAR}	/* 채널 코드, VARCHAR2(10) */
		AND RESERV_NUM = #{reservNum, jdbcType=DECIMAL}	/* 예약 번호, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 CPN_CD	/* 쿠폰 코드, NUMBER(0,22) */
			,USER_ID	/* 회원 ID, VARCHAR2(20) */
			,CHANNEL_CD	/* 채널 코드, VARCHAR2(10) */
			,RESERV_NUM	/* 예약 번호, NUMBER(0,22) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{cpnCd, jdbcType=DECIMAL}
			,#{userId, jdbcType=VARCHAR}
			,#{channelCd, jdbcType=VARCHAR}
			,#{reservNum, jdbcType=DECIMAL}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
