mapper.insert:
INSERT INTO EZC_DEPOSIT_ISSUE_HIST(	/* 예치금 발생 내역 */
	 DEPOSIT_ISSUE_SEQ	/* 예치금 발생 일련번호, NUMBER(0,22) */
	,PARTNER_CD	/* 제휴사 코드, VARCHAR2(20) */
	,RESERV_NUM	/* 예약 번호, NUMBER(0,22) */
	,ISSUE_DD	/* 발생 일자, VARCHAR2(8) */
	,ISSUE_DEPOSIT_AMT	/* 발생 예치 금액, NUMBER(0,22) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{depositIssueSeq, jdbcType=DECIMAL}
	,#{partnerCd, jdbcType=VARCHAR}
	,#{reservNum, jdbcType=DECIMAL}
	,#{issueDd, jdbcType=VARCHAR}
	,#{issueDepositAmt, jdbcType=NUMERIC}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_DEPOSIT_ISSUE_HIST /* 예치금 발생 내역 */
	USING DUAL ON (
		DEPOSIT_ISSUE_SEQ = #{depositIssueSeq, jdbcType=DECIMAL}	/* 예치금 발생 일련번호, NUMBER(0,22) */
		AND PARTNER_CD = #{partnerCd, jdbcType=VARCHAR}	/* 제휴사 코드, VARCHAR2(20) */
		AND RESERV_NUM = #{reservNum, jdbcType=DECIMAL}	/* 예약 번호, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 ISSUE_DD = #{issueDd, jdbcType=VARCHAR}	/* 발생 일자, VARCHAR2(8) */
			,ISSUE_DEPOSIT_AMT = #{issueDepositAmt, jdbcType=NUMERIC}	/* 발생 예치 금액, NUMBER(0,22) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 DEPOSIT_ISSUE_SEQ	/* 예치금 발생 일련번호, NUMBER(0,22) */
			,PARTNER_CD	/* 제휴사 코드, VARCHAR2(20) */
			,RESERV_NUM	/* 예약 번호, NUMBER(0,22) */
			,ISSUE_DD	/* 발생 일자, VARCHAR2(8) */
			,ISSUE_DEPOSIT_AMT	/* 발생 예치 금액, NUMBER(0,22) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{depositIssueSeq, jdbcType=DECIMAL}
			,#{partnerCd, jdbcType=VARCHAR}
			,#{reservNum, jdbcType=DECIMAL}
			,#{issueDd, jdbcType=VARCHAR}
			,#{issueDepositAmt, jdbcType=NUMERIC}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
