mapper.insert:
INSERT INTO EZC_DEPOSIT(	/* 예치금 */
	 PARTNER_CD	/* 제휴사 코드, VARCHAR2(20) */
	,DEPOSIT_AMT	/* 예치 금액, NUMBER(0,22) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{partnerCd, jdbcType=VARCHAR}
	,#{depositAmt, jdbcType=NUMERIC}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS ROW__NUM, RECORDS.* FROM ( /* #### SQL Body [[ ################# */ SELECT EZC_DEPOSIT.PARTNER_CD AS partnerCd /* 제휴사 코드, VARCHAR2(20) */ ,EZC_DEPOSIT.DEPOSIT_AMT AS depositAmt /* 예치 금액, NUMBER(0,22) */ ,EZC_DEPOSIT.REG_ID AS regId /* 등록자 ID, VARCHAR2(20) */ ,EZC_DEPOSIT.REG_DT AS regDt /* 등록 일시, VARCHAR2(14) */ ,EZC_DEPOSIT.MODI_ID AS modiId /* 수정자 ID, VARCHAR2(20) */ ,EZC_DEPOSIT.MODI_DT AS modiDt /* 수정 일시, VARCHAR2(14) */ FROM EZC_DEPOSIT /* 예치금 */ ,EZC_PARTNER /* 제휴사 정보 */ WHERE 1=1 AND EZC_DEPOSIT.PARTNER_CD = EZC_PARTNER.PARTNER_CD AND EZC_DEPOSIT.REC_STAT = #{param.recStat, jdbcType=CHAR} /* 레코드 상태, CHAR(1) */ AND EZC_PARTNER.REC_STAT = #{param.recStat, jdbcType=CHAR} /* 레코드 상태, CHAR(1) */
AND EZC_DEPOSIT.PARTNER_CD = #{param.partnerCd, jdbcType=VARCHAR} /* 제휴사 코드, VARCHAR2(20) */
ORDER BY EZC_DEPOSIT.PARTNER_CD DESC /* #### SQL Body ]] ################# */ ) RECORDS WHERE ROWNUM <= ((#{param.pageNum, jdbcType=NUMERIC}*#{param.pageCount, jdbcType=NUMERIC})+1) ) WHERE ROW__NUM > (#{param.pageNum, jdbcType=NUMERIC}-1)*#{param.pageCount, jdbcType=NUMERIC}

mapper.update:
MERGE INTO EZC_DEPOSIT /* 예치금 */
	USING DUAL ON (
		PARTNER_CD = #{partnerCd, jdbcType=VARCHAR}	/* 제휴사 코드, VARCHAR2(20) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 DEPOSIT_AMT = #{depositAmt, jdbcType=NUMERIC}	/* 예치 금액, NUMBER(0,22) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 PARTNER_CD	/* 제휴사 코드, VARCHAR2(20) */
			,DEPOSIT_AMT	/* 예치 금액, NUMBER(0,22) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{partnerCd, jdbcType=VARCHAR}
			,#{depositAmt, jdbcType=NUMERIC}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
