mapper.insert:
INSERT INTO EZC_DEPOSIT_USE_HIST(	/* 예치금 사용 내역 */
	 DEPOSIT_USE_SEQ	/* 예치금 사용 일련번호, NUMBER(0,22) */
	,PARTNER_CD	/* 제휴사 코드, VARCHAR2(20) */
	,RESERV_NUM	/* 예약 번호, NUMBER(0,22) */
	,PAY_DIV	/* 지불 구분, VARCHAR2(8) */
	,PAY_TRANS_DD	/* 송금 일자, VARCHAR2(8) */
	,USE_DEPOSIT_AMT	/* 사용 예치 금액, NUMBER(0,22) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{depositUseSeq, jdbcType=DECIMAL}
	,#{partnerCd, jdbcType=VARCHAR}
	,#{reservNum, jdbcType=DECIMAL}
	,#{payDiv, jdbcType=VARCHAR}
	,#{payTransDd, jdbcType=VARCHAR}
	,#{useDepositAmt, jdbcType=NUMERIC}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_DEPOSIT_USE_HIST /* 예치금 사용 내역 */
	USING DUAL ON (
		DEPOSIT_USE_SEQ = #{depositUseSeq, jdbcType=DECIMAL}	/* 예치금 사용 일련번호, NUMBER(0,22) */
		AND PARTNER_CD = #{partnerCd, jdbcType=VARCHAR}	/* 제휴사 코드, VARCHAR2(20) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 RESERV_NUM = #{reservNum, jdbcType=DECIMAL}	/* 예약 번호, NUMBER(0,22) */
			,PAY_DIV = #{payDiv, jdbcType=VARCHAR}	/* 지불 구분, VARCHAR2(8) */
			,PAY_TRANS_DD = #{payTransDd, jdbcType=VARCHAR}	/* 송금 일자, VARCHAR2(8) */
			,USE_DEPOSIT_AMT = #{useDepositAmt, jdbcType=NUMERIC}	/* 사용 예치 금액, NUMBER(0,22) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 DEPOSIT_USE_SEQ	/* 예치금 사용 일련번호, NUMBER(0,22) */
			,PARTNER_CD	/* 제휴사 코드, VARCHAR2(20) */
			,RESERV_NUM	/* 예약 번호, NUMBER(0,22) */
			,PAY_DIV	/* 지불 구분, VARCHAR2(8) */
			,PAY_TRANS_DD	/* 송금 일자, VARCHAR2(8) */
			,USE_DEPOSIT_AMT	/* 사용 예치 금액, NUMBER(0,22) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{depositUseSeq, jdbcType=DECIMAL}
			,#{partnerCd, jdbcType=VARCHAR}
			,#{reservNum, jdbcType=DECIMAL}
			,#{payDiv, jdbcType=VARCHAR}
			,#{payTransDd, jdbcType=VARCHAR}
			,#{useDepositAmt, jdbcType=NUMERIC}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
