mapper.insert:
INSERT INTO EZC_DETAIL_CD(	/* 공통 코드 정보 */
	 MASTER_CD	/* 공통 코드, VARCHAR2(8) */
	,DETAIL_CD	/* 상세 코드, VARCHAR2(4) */
	,CLASS_CD	/* 분류 코드, VARCHAR2(4) */
	,DETAIL_NM	/* 상세 명, VARCHAR2(50) */
	,DISP_ORDER	/* 정렬순서, NUMBER(0,22) */
	,USE_YN	/* 사용 여부, CHAR(1) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{masterCd, jdbcType=VARCHAR}
	,#{detailCd, jdbcType=VARCHAR}
	,#{classCd, jdbcType=VARCHAR}
	,#{detailNm, jdbcType=VARCHAR}
	,#{dispOrder, jdbcType=NUMERIC}
	,#{useYn, jdbcType=CHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_DETAIL_CD /* 공통 코드 정보 */
	USING DUAL ON (
		MASTER_CD = #{masterCd, jdbcType=VARCHAR}	/* 공통 코드, VARCHAR2(8) */
		AND CLASS_CD = #{classCd, jdbcType=VARCHAR}	/* 분류 코드, VARCHAR2(4) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 DETAIL_CD = #{detailCd, jdbcType=VARCHAR}	/* 상세 코드, VARCHAR2(4) */
			,DETAIL_NM = #{detailNm, jdbcType=VARCHAR}	/* 상세 명, VARCHAR2(50) */
			,DISP_ORDER = #{dispOrder, jdbcType=NUMERIC}	/* 정렬순서, NUMBER(0,22) */
			,USE_YN = #{useYn, jdbcType=CHAR}	/* 사용 여부, CHAR(1) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 MASTER_CD	/* 공통 코드, VARCHAR2(8) */
			,DETAIL_CD	/* 상세 코드, VARCHAR2(4) */
			,CLASS_CD	/* 분류 코드, VARCHAR2(4) */
			,DETAIL_NM	/* 상세 명, VARCHAR2(50) */
			,DISP_ORDER	/* 정렬순서, NUMBER(0,22) */
			,USE_YN	/* 사용 여부, CHAR(1) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{masterCd, jdbcType=VARCHAR}
			,#{detailCd, jdbcType=VARCHAR}
			,#{classCd, jdbcType=VARCHAR}
			,#{detailNm, jdbcType=VARCHAR}
			,#{dispOrder, jdbcType=NUMERIC}
			,#{useYn, jdbcType=CHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
