mapper.insert:
INSERT INTO EZC_DISP(	/* 전시 */
	 DISP_CD	/* 전시 코드, NUMBER(0,22) */
	,DISP_NM	/* 전시 명, VARCHAR2(100) */
	,DISP_DESC	/* 전시 설명, VARCHAR2(4000) */
	,IMG_PATH	/* 이미지 경로, VARCHAR2(100) */
	,IMG_SYS_FILENM	/* 이미지 시스템 파일명, VARCHAR2(100) */
	,IMG_FILENM	/* 이미지 파일명, VARCHAR2(100) */
	,URL	/* URL, VARCHAR2(200) */
	,IMG_HTML	/* 이미지 HTML, VARCHAR2(4000) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{dispCd, jdbcType=DECIMAL}
	,#{dispNm, jdbcType=VARCHAR}
	,#{dispDesc, jdbcType=VARCHAR}
	,#{imgPath, jdbcType=VARCHAR}
	,#{imgSysFilenm, jdbcType=VARCHAR}
	,#{imgFilenm, jdbcType=VARCHAR}
	,#{url, jdbcType=VARCHAR}
	,#{imgHtml, jdbcType=VARCHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_DISP /* 전시 */
	USING DUAL ON (
		DISP_CD = #{dispCd, jdbcType=DECIMAL}	/* 전시 코드, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 DISP_NM = #{dispNm, jdbcType=VARCHAR}	/* 전시 명, VARCHAR2(100) */
			,DISP_DESC = #{dispDesc, jdbcType=VARCHAR}	/* 전시 설명, VARCHAR2(4000) */
			,IMG_PATH = #{imgPath, jdbcType=VARCHAR}	/* 이미지 경로, VARCHAR2(100) */
			,IMG_SYS_FILENM = #{imgSysFilenm, jdbcType=VARCHAR}	/* 이미지 시스템 파일명, VARCHAR2(100) */
			,IMG_FILENM = #{imgFilenm, jdbcType=VARCHAR}	/* 이미지 파일명, VARCHAR2(100) */
			,URL = #{url, jdbcType=VARCHAR}	/* URL, VARCHAR2(200) */
			,IMG_HTML = #{imgHtml, jdbcType=VARCHAR}	/* 이미지 HTML, VARCHAR2(4000) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 DISP_CD	/* 전시 코드, NUMBER(0,22) */
			,DISP_NM	/* 전시 명, VARCHAR2(100) */
			,DISP_DESC	/* 전시 설명, VARCHAR2(4000) */
			,IMG_PATH	/* 이미지 경로, VARCHAR2(100) */
			,IMG_SYS_FILENM	/* 이미지 시스템 파일명, VARCHAR2(100) */
			,IMG_FILENM	/* 이미지 파일명, VARCHAR2(100) */
			,URL	/* URL, VARCHAR2(200) */
			,IMG_HTML	/* 이미지 HTML, VARCHAR2(4000) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{dispCd, jdbcType=DECIMAL}
			,#{dispNm, jdbcType=VARCHAR}
			,#{dispDesc, jdbcType=VARCHAR}
			,#{imgPath, jdbcType=VARCHAR}
			,#{imgSysFilenm, jdbcType=VARCHAR}
			,#{imgFilenm, jdbcType=VARCHAR}
			,#{url, jdbcType=VARCHAR}
			,#{imgHtml, jdbcType=VARCHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
