mapper.insert:
INSERT INTO EZC_DIST_CANCEL_RULE(	/* 직영/숙박 취소 규정 */
	 ROOM_CD	/* 객실 코드, NUMBER(0,22) */
	,PENALTY_ISSUEDD_CNT	/* 패널티 발생일 수, NUMBER(0,22) */
	,FEE_RATE	/* 수수료 율, NUMBER(0,22) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{roomCd, jdbcType=DECIMAL}
	,#{penaltyIssueddCnt, jdbcType=NUMERIC}
	,#{feeRate, jdbcType=NUMERIC}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_DIST_CANCEL_RULE /* 직영/숙박 취소 규정 */
	USING DUAL ON (
		ROOM_CD = #{roomCd, jdbcType=DECIMAL}	/* 객실 코드, NUMBER(0,22) */
		AND PENALTY_ISSUEDD_CNT = #{penaltyIssueddCnt, jdbcType=NUMERIC}	/* 패널티 발생일 수, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 FEE_RATE = #{feeRate, jdbcType=NUMERIC}	/* 수수료 율, NUMBER(0,22) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 ROOM_CD	/* 객실 코드, NUMBER(0,22) */
			,PENALTY_ISSUEDD_CNT	/* 패널티 발생일 수, NUMBER(0,22) */
			,FEE_RATE	/* 수수료 율, NUMBER(0,22) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{roomCd, jdbcType=DECIMAL}
			,#{penaltyIssueddCnt, jdbcType=NUMERIC}
			,#{feeRate, jdbcType=NUMERIC}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
