mapper.insert:
INSERT INTO EZC_DIST_FACL_DETAIL(	/* 직영/숙박 시설 상세 */
	 FACL_CD	/* 시설 코드, NUMBER(0,22) */
	,FAX_NUM	/* 팩스 번호, VARCHAR2(20) */
	,HOMEPAGE	/* 홈페이지, VARCHAR2(200) */
	,ADD_BREAKFAST_AMT	/* 추가 조식 요금, NUMBER(0,22) */
	,ADD_USER_AMT	/* 추가 인원 요금, NUMBER(0,22) */
	,ADD_BED_AMT	/* 추가 배드 요금, NUMBER(0,22) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{faclCd, jdbcType=DECIMAL}
	,#{faxNum, jdbcType=VARCHAR}
	,#{homepage, jdbcType=VARCHAR}
	,#{addBreakfastAmt, jdbcType=NUMERIC}
	,#{addUserAmt, jdbcType=NUMERIC}
	,#{addBedAmt, jdbcType=NUMERIC}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_DIST_FACL_DETAIL /* 직영/숙박 시설 상세 */
	USING DUAL ON (
		FACL_CD = #{faclCd, jdbcType=DECIMAL}	/* 시설 코드, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 FAX_NUM = #{faxNum, jdbcType=VARCHAR}	/* 팩스 번호, VARCHAR2(20) */
			,HOMEPAGE = #{homepage, jdbcType=VARCHAR}	/* 홈페이지, VARCHAR2(200) */
			,ADD_BREAKFAST_AMT = #{addBreakfastAmt, jdbcType=NUMERIC}	/* 추가 조식 요금, NUMBER(0,22) */
			,ADD_USER_AMT = #{addUserAmt, jdbcType=NUMERIC}	/* 추가 인원 요금, NUMBER(0,22) */
			,ADD_BED_AMT = #{addBedAmt, jdbcType=NUMERIC}	/* 추가 배드 요금, NUMBER(0,22) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 FACL_CD	/* 시설 코드, NUMBER(0,22) */
			,FAX_NUM	/* 팩스 번호, VARCHAR2(20) */
			,HOMEPAGE	/* 홈페이지, VARCHAR2(200) */
			,ADD_BREAKFAST_AMT	/* 추가 조식 요금, NUMBER(0,22) */
			,ADD_USER_AMT	/* 추가 인원 요금, NUMBER(0,22) */
			,ADD_BED_AMT	/* 추가 배드 요금, NUMBER(0,22) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{faclCd, jdbcType=DECIMAL}
			,#{faxNum, jdbcType=VARCHAR}
			,#{homepage, jdbcType=VARCHAR}
			,#{addBreakfastAmt, jdbcType=NUMERIC}
			,#{addUserAmt, jdbcType=NUMERIC}
			,#{addBedAmt, jdbcType=NUMERIC}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
