mapper.insert:
INSERT INTO EZC_DIST_FACL_DETAIL(	/* 직영/숙박 시설 상세 */
	 FACL_CD	/* 시설 코드, NUMBER(0,22) */
	,FAX_NUM	/* 팩스 번호, VARCHAR2(20) */
	,HOMEPAGE	/* 홈페이지, VARCHAR2(200) */
	,ADD_BREAKFAST_AMT	/* 추가 조식 요금, NUMBER(0,22) */
	,ADD_USER_AMT	/* 추가 인원 요금, NUMBER(0,22) */
	,ADD_BED_AMT	/* 추가 배드 요금, NUMBER(0,22) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{faclCd, jdbcType=DECIMAL}
	,#{faxNum, jdbcType=VARCHAR}
	,#{homepage, jdbcType=VARCHAR}
	,#{addBreakfastAmt, jdbcType=NUMERIC}
	,#{addUserAmt, jdbcType=NUMERIC}
	,#{addBedAmt, jdbcType=NUMERIC}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS ROW__NUM, RECORDS.* FROM ( /* #### SQL Body [[ ################# */ SELECT EZC_DIST_FACL_DETAIL.FACL_CD AS faclCd /* 시설 코드, NUMBER(0,22) */ ,EZC_DIST_FACL_DETAIL.FAX_NUM AS faxNum /* 팩스 번호, VARCHAR2(20) */ ,EZC_DIST_FACL_DETAIL.HOMEPAGE AS homepage /* 홈페이지, VARCHAR2(200) */ ,EZC_DIST_FACL_DETAIL.ADD_BREAKFAST_AMT AS addBreakfastAmt /* 추가 조식 요금, NUMBER(0,22) */ ,EZC_DIST_FACL_DETAIL.ADD_USER_AMT AS addUserAmt /* 추가 인원 요금, NUMBER(0,22) */ ,EZC_DIST_FACL_DETAIL.ADD_BED_AMT AS addBedAmt /* 추가 배드 요금, NUMBER(0,22) */ ,EZC_DIST_FACL_DETAIL.REG_ID AS regId /* 등록자 ID, VARCHAR2(20) */ ,EZC_DIST_FACL_DETAIL.REG_DT AS regDt /* 등록 일시, VARCHAR2(14) */ ,EZC_DIST_FACL_DETAIL.MODI_ID AS modiId /* 수정자 ID, VARCHAR2(20) */ ,EZC_DIST_FACL_DETAIL.MODI_DT AS modiDt /* 수정 일시, VARCHAR2(14) */ FROM EZC_DIST_FACL_DETAIL /* 직영/숙박 시설 상세 */ ,EZC_FACL /* 시설 정보 */ ,EZC_AREA_CD /* 지역 코드 */ ,EZC_CITY_CD /* 도시 코드 */ ,EZC_PARTNER /* 제휴사 정보 */ WHERE 1=1 AND EZC_DIST_FACL_DETAIL.FACL_CD = EZC_FACL.FACL_CD AND EZC_FACL.AREA_CD = EZC_AREA_CD.AREA_CD AND EZC_AREA_CD.CITY_CD = EZC_CITY_CD.CITY_CD AND EZC_FACL.CITY_CD = EZC_CITY_CD.CITY_CD AND EZC_FACL.PARTNER_CD = EZC_PARTNER.PARTNER_CD AND EZC_DIST_FACL_DETAIL.REC_STAT = #{param.recStat, jdbcType=CHAR} /* 레코드 상태, CHAR(1) */ AND EZC_FACL.REC_STAT = #{param.recStat, jdbcType=CHAR} /* 레코드 상태, CHAR(1) */ AND EZC_AREA_CD.REC_STAT = #{param.recStat, jdbcType=CHAR} /* 레코드 상태, CHAR(1) */ AND EZC_CITY_CD.REC_STAT = #{param.recStat, jdbcType=CHAR} /* 레코드 상태, CHAR(1) */ AND EZC_PARTNER.REC_STAT = #{param.recStat, jdbcType=CHAR} /* 레코드 상태, CHAR(1) */
AND EZC_DIST_FACL_DETAIL.FACL_CD = #{param.faclCd, jdbcType=DECIMAL} /* 시설 코드, NUMBER(0,22) */
ORDER BY EZC_DIST_FACL_DETAIL.FACL_CD DESC /* #### SQL Body ]] ################# */ ) RECORDS WHERE ROWNUM <= ((#{param.pageNum, jdbcType=NUMERIC}*#{param.pageCount, jdbcType=NUMERIC})+1) ) WHERE ROW__NUM > (#{param.pageNum, jdbcType=NUMERIC}-1)*#{param.pageCount, jdbcType=NUMERIC}

mapper.update:
MERGE INTO EZC_DIST_FACL_DETAIL /* 직영/숙박 시설 상세 */
	USING DUAL ON (
		FACL_CD = #{faclCd, jdbcType=DECIMAL}	/* 시설 코드, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 FAX_NUM = #{faxNum, jdbcType=VARCHAR}	/* 팩스 번호, VARCHAR2(20) */
			,HOMEPAGE = #{homepage, jdbcType=VARCHAR}	/* 홈페이지, VARCHAR2(200) */
			,ADD_BREAKFAST_AMT = #{addBreakfastAmt, jdbcType=NUMERIC}	/* 추가 조식 요금, NUMBER(0,22) */
			,ADD_USER_AMT = #{addUserAmt, jdbcType=NUMERIC}	/* 추가 인원 요금, NUMBER(0,22) */
			,ADD_BED_AMT = #{addBedAmt, jdbcType=NUMERIC}	/* 추가 배드 요금, NUMBER(0,22) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 FACL_CD	/* 시설 코드, NUMBER(0,22) */
			,FAX_NUM	/* 팩스 번호, VARCHAR2(20) */
			,HOMEPAGE	/* 홈페이지, VARCHAR2(200) */
			,ADD_BREAKFAST_AMT	/* 추가 조식 요금, NUMBER(0,22) */
			,ADD_USER_AMT	/* 추가 인원 요금, NUMBER(0,22) */
			,ADD_BED_AMT	/* 추가 배드 요금, NUMBER(0,22) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{faclCd, jdbcType=DECIMAL}
			,#{faxNum, jdbcType=VARCHAR}
			,#{homepage, jdbcType=VARCHAR}
			,#{addBreakfastAmt, jdbcType=NUMERIC}
			,#{addUserAmt, jdbcType=NUMERIC}
			,#{addBedAmt, jdbcType=NUMERIC}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
