mapper.insert:
INSERT INTO EZC_DIST_ROOM_AMT(	/* 직영/숙박 객실 금액 */
	 ROOM_CD	/* 객실 코드, NUMBER(0,22) */
	,APPLY_DD	/* 적용 일자, VARCHAR2(8) */
	,TOT_ROOM_CNT	/* 총 객실 수, NUMBER(0,22) */
	,SALE_ROOM_CNT	/* 판매 객실 수, NUMBER(0,22) */
	,SALE_AMT	/* 판매 금액, NUMBER(0,22) */
	,AUTO_END_YN	/* 자동 마감 여부, CHAR(1) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{roomCd, jdbcType=DECIMAL}
	,#{applyDd, jdbcType=VARCHAR}
	,#{totRoomCnt, jdbcType=NUMERIC}
	,#{saleRoomCnt, jdbcType=NUMERIC}
	,#{saleAmt, jdbcType=NUMERIC}
	,#{autoEndYn, jdbcType=CHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_DIST_ROOM_AMT /* 직영/숙박 객실 금액 */
	USING DUAL ON (
		ROOM_CD = #{roomCd, jdbcType=DECIMAL}	/* 객실 코드, NUMBER(0,22) */
		AND APPLY_DD = #{applyDd, jdbcType=VARCHAR}	/* 적용 일자, VARCHAR2(8) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 TOT_ROOM_CNT = #{totRoomCnt, jdbcType=NUMERIC}	/* 총 객실 수, NUMBER(0,22) */
			,SALE_ROOM_CNT = #{saleRoomCnt, jdbcType=NUMERIC}	/* 판매 객실 수, NUMBER(0,22) */
			,SALE_AMT = #{saleAmt, jdbcType=NUMERIC}	/* 판매 금액, NUMBER(0,22) */
			,AUTO_END_YN = #{autoEndYn, jdbcType=CHAR}	/* 자동 마감 여부, CHAR(1) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 ROOM_CD	/* 객실 코드, NUMBER(0,22) */
			,APPLY_DD	/* 적용 일자, VARCHAR2(8) */
			,TOT_ROOM_CNT	/* 총 객실 수, NUMBER(0,22) */
			,SALE_ROOM_CNT	/* 판매 객실 수, NUMBER(0,22) */
			,SALE_AMT	/* 판매 금액, NUMBER(0,22) */
			,AUTO_END_YN	/* 자동 마감 여부, CHAR(1) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{roomCd, jdbcType=DECIMAL}
			,#{applyDd, jdbcType=VARCHAR}
			,#{totRoomCnt, jdbcType=NUMERIC}
			,#{saleRoomCnt, jdbcType=NUMERIC}
			,#{saleAmt, jdbcType=NUMERIC}
			,#{autoEndYn, jdbcType=CHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
