mapper.insert:
INSERT INTO EZC_DIST_ROOM(	/* 직영/숙박 객실 */
	 ROOM_CD	/* 객실 코드, NUMBER(0,22) */
	,FACL_CD	/* 시설 코드, NUMBER(0,22) */
	,SPEC_YN	/* 특가 여부, CHAR(1) */
	,DAY_PRICE_YN	/* 당일특가 여부, CHAR(1) */
