	,BREAKFAST_INCLUDE_YN	/* 조식 포함 여부, CHAR(1) */
	,VIEWER	/* 전망, VARCHAR2(1000) */
	,ROOM_SIZE	/* 객실 크기, VARCHAR2(100) */
	,ROOM_SVC	/* 객실 서비스, VARCHAR2(1000) */
	,ROOM_OFFER_ITEM	/* 객실 구비 품목, VARCHAR2(1000) */
	,BATH_OFFER_ITEM	/* 욕실 구비 품목, VARCHAR2(1000) */
	,BATH_CNT	/* 욕실 수, NUMBER(0,22) */
	,LIVING_CNT	/* 거실 수, NUMBER(0,22) */
	,KITCHEN_CNT	/* 주방 수, NUMBER(0,22) */
	,TERRACE_CNT	/* 테라스 수, NUMBER(0,22) */
	,ROOM_INFO	/* 객실 정보, VARCHAR2(1000) */
	,CANCEL_FEE_RULE	/* 취소 수수료 규정, VARCHAR2(4000) */
	,LOMT_CNT	/* 숙박 수, NUMBER(0,22) */
	,CUTOFF_DDCNT	/* 컷오프 일수, NUMBER(0,22) */
	,TERM_DDCNT	/* 텀 일수, NUMBER(0,22) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{roomCd, jdbcType=DECIMAL}
	,#{faclCd, jdbcType=DECIMAL}
	,#{specYn, jdbcType=CHAR}
	,#{dayPriceYn, jdbcType=CHAR}
	,#{roomNm, jdbcType=VARCHAR}
	,#{normalAmt, jdbcType=NUMERIC}
	,#{stdUserCnt, jdbcType=NUMERIC}
	,#{maxUserCnt, jdbcType=NUMERIC}
	,#{breakfastIncludeYn, jdbcType=CHAR}
	,#{viewer, jdbcType=VARCHAR}
	,#{roomSize, jdbcType=VARCHAR}
	,#{roomSvc, jdbcType=VARCHAR}
	,#{roomOfferItem, jdbcType=VARCHAR}
	,#{bathOfferItem, jdbcType=VARCHAR}
