mapper.update:
MERGE INTO EZC_DIST_ROOM /* 직영/숙박 객실 */
	USING DUAL ON (
		ROOM_CD = #{roomCd, jdbcType=DECIMAL}	/* 객실 코드, NUMBER(0,22) */
		AND FACL_CD = #{faclCd, jdbcType=DECIMAL}	/* 시설 코드, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 SPEC_YN = #{specYn, jdbcType=CHAR}	/* 특가 여부, CHAR(1) */
			,DAY_PRICE_YN = #{dayPriceYn, jdbcType=CHAR}	/* 당일특가 여부, CHAR(1) */
			,ROOM_NM = #{roomNm, jdbcType=VARCHAR}	/* 객실 명, VARCHAR2(100) */
			,NORMAL_AMT = #{normalAmt, jdbcType=NUMERIC}	/* 정상 금액, NUMBER(0,22) */
			,STD_USER_CNT = #{stdUserCnt, jdbcType=NUMERIC}	/* 기준 인원 수, NUMBER(0,22) */
			,MAX_USER_CNT = #{maxUserCnt, jdbcType=NUMERIC}	/* 최대 인원 수, NUMBER(0,22) */
			,BREAKFAST_INCLUDE_YN = #{breakfastIncludeYn, jdbcType=CHAR}	/* 조식 포함 여부, CHAR(1) */
			,VIEWER = #{viewer, jdbcType=VARCHAR}	/* 전망, VARCHAR2(1000) */
			,ROOM_SIZE = #{roomSize, jdbcType=VARCHAR}	/* 객실 크기, VARCHAR2(100) */
			,ROOM_SVC = #{roomSvc, jdbcType=VARCHAR}	/* 객실 서비스, VARCHAR2(1000) */
			,ROOM_OFFER_ITEM = #{roomOfferItem, jdbcType=VARCHAR}	/* 객실 구비 품목, VARCHAR2(1000) */
			,BATH_OFFER_ITEM = #{bathOfferItem, jdbcType=VARCHAR}	/* 욕실 구비 품목, VARCHAR2(1000) */
			,BATH_CNT = #{bathCnt, jdbcType=NUMERIC}	/* 욕실 수, NUMBER(0,22) */
			,LIVING_CNT = #{livingCnt, jdbcType=NUMERIC}	/* 거실 수, NUMBER(0,22) */
			,KITCHEN_CNT = #{kitchenCnt, jdbcType=NUMERIC}	/* 주방 수, NUMBER(0,22) */
			,TERRACE_CNT = #{terraceCnt, jdbcType=NUMERIC}	/* 테라스 수, NUMBER(0,22) */
			,ROOM_INFO = #{roomInfo, jdbcType=VARCHAR}	/* 객실 정보, VARCHAR2(1000) */
			,CANCEL_FEE_RULE = #{cancelFeeRule, jdbcType=VARCHAR}	/* 취소 수수료 규정, VARCHAR2(4000) */
			,LOMT_CNT = #{lomtCnt, jdbcType=NUMERIC}	/* 숙박 수, NUMBER(0,22) */
			,CUTOFF_DDCNT = #{cutoffDdcnt, jdbcType=NUMERIC}	/* 컷오프 일수, NUMBER(0,22) */
			,TERM_DDCNT = #{termDdcnt, jdbcType=NUMERIC}	/* 텀 일수, NUMBER(0,22) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 ROOM_CD	/* 객실 코드, NUMBER(0,22) */
			,FACL_CD	/* 시설 코드, NUMBER(0,22) */
