			,NORMAL_AMT	/* 정상 금액, NUMBER(0,22) */
			,STD_USER_CNT	/* 기준 인원 수, NUMBER(0,22) */
			,MAX_USER_CNT	/* 최대 인원 수, NUMBER(0,22) */
			,BREAKFAST_INCLUDE_YN	/* 조식 포함 여부, CHAR(1) */
			,VIEWER	/* 전망, VARCHAR2(1000) */
			,ROOM_SIZE	/* 객실 크기, VARCHAR2(100) */
			,ROOM_SVC	/* 객실 서비스, VARCHAR2(1000) */
			,ROOM_OFFER_ITEM	/* 객실 구비 품목, VARCHAR2(1000) */
			,BATH_OFFER_ITEM	/* 욕실 구비 품목, VARCHAR2(1000) */
			,BATH_CNT	/* 욕실 수, NUMBER(0,22) */
			,LIVING_CNT	/* 거실 수, NUMBER(0,22) */
			,KITCHEN_CNT	/* 주방 수, NUMBER(0,22) */
