mapper.insert:
INSERT INTO EZC_DIST_ROOM_OPT(	/* 직영/숙박 객실 옵션 */
	 ROOM_CD	/* 객실 코드, NUMBER(0,22) */
	,ROOM_OPTCD	/* 객실 옵션코드, VARCHAR2(100) */
	,OPT_NM	/* 옵션 이름, VARCHAR2(50) */
	,OPT_AMT	/* 옵션 금액, NUMBER(0,22) */
	,LIMIT_QTY	/* 최대 가능 수량, NUMBER(0,22) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{roomCd, jdbcType=DECIMAL}
	,#{roomOptcd, jdbcType=VARCHAR}
	,#{optNm, jdbcType=VARCHAR}
	,#{optAmt, jdbcType=NUMERIC}
	,#{limitQty, jdbcType=NUMERIC}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_DIST_ROOM_OPT /* 직영/숙박 객실 옵션 */
	USING DUAL ON (
		ROOM_CD = #{roomCd, jdbcType=DECIMAL}	/* 객실 코드, NUMBER(0,22) */
		AND ROOM_OPTCD = #{roomOptcd, jdbcType=VARCHAR}	/* 객실 옵션코드, VARCHAR2(100) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 OPT_NM = #{optNm, jdbcType=VARCHAR}	/* 옵션 이름, VARCHAR2(50) */
			,OPT_AMT = #{optAmt, jdbcType=NUMERIC}	/* 옵션 금액, NUMBER(0,22) */
			,LIMIT_QTY = #{limitQty, jdbcType=NUMERIC}	/* 최대 가능 수량, NUMBER(0,22) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 ROOM_CD	/* 객실 코드, NUMBER(0,22) */
			,ROOM_OPTCD	/* 객실 옵션코드, VARCHAR2(100) */
			,OPT_NM	/* 옵션 이름, VARCHAR2(50) */
			,OPT_AMT	/* 옵션 금액, NUMBER(0,22) */
			,LIMIT_QTY	/* 최대 가능 수량, NUMBER(0,22) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{roomCd, jdbcType=DECIMAL}
			,#{roomOptcd, jdbcType=VARCHAR}
			,#{optNm, jdbcType=VARCHAR}
			,#{optAmt, jdbcType=NUMERIC}
			,#{limitQty, jdbcType=NUMERIC}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
