mapper.insert:
INSERT INTO EZC_FACL_IMG(	/* 시설 이미지 */
	 FACL_IMG_SEQ	/* 시설 이미지 일련번호, NUMBER(0,22) */
	,FACL_CD	/* 시설 코드, NUMBER(0,22) */
	,FACL_IMG_TYPE	/* 시설 이미지 유형, VARCHAR2(8) */
	,MAIN_IMG_YN	/* 대표 이미지 여부, CHAR(1) */
	,IMG_URL	/* 이미지 URL, VARCHAR2(200) */
	,IMG_DESC	/* 이미지 설명, VARCHAR2(1000) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
	,PARTNER_IMG_URL	/* 이미지 경로, VARCHAR2(500) */
) VALUES (
	 #{faclImgSeq, jdbcType=DECIMAL}
	,#{faclCd, jdbcType=DECIMAL}
	,#{faclImgType, jdbcType=VARCHAR}
	,#{mainImgYn, jdbcType=CHAR}
	,#{imgUrl, jdbcType=VARCHAR}
	,#{imgDesc, jdbcType=VARCHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
	,#{partnerImgUrl, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_FACL_IMG /* 시설 이미지 */
	USING DUAL ON (
		FACL_IMG_SEQ = #{faclImgSeq, jdbcType=DECIMAL}	/* 시설 이미지 일련번호, NUMBER(0,22) */
		AND FACL_CD = #{faclCd, jdbcType=DECIMAL}	/* 시설 코드, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 FACL_IMG_TYPE = #{faclImgType, jdbcType=VARCHAR}	/* 시설 이미지 유형, VARCHAR2(8) */
			,MAIN_IMG_YN = #{mainImgYn, jdbcType=CHAR}	/* 대표 이미지 여부, CHAR(1) */
			,IMG_URL = #{imgUrl, jdbcType=VARCHAR}	/* 이미지 URL, VARCHAR2(200) */
			,IMG_DESC = #{imgDesc, jdbcType=VARCHAR}	/* 이미지 설명, VARCHAR2(1000) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
			,PARTNER_IMG_URL = #{partnerImgUrl, jdbcType=VARCHAR}	/* 이미지 경로, VARCHAR2(500) */
	WHEN NOT MATCHED THEN
		INSERT (
			 FACL_IMG_SEQ	/* 시설 이미지 일련번호, NUMBER(0,22) */
			,FACL_CD	/* 시설 코드, NUMBER(0,22) */
			,FACL_IMG_TYPE	/* 시설 이미지 유형, VARCHAR2(8) */
			,MAIN_IMG_YN	/* 대표 이미지 여부, CHAR(1) */
			,IMG_URL	/* 이미지 URL, VARCHAR2(200) */
			,IMG_DESC	/* 이미지 설명, VARCHAR2(1000) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
			,PARTNER_IMG_URL	/* 이미지 경로, VARCHAR2(500) */
		) VALUES (
			 #{faclImgSeq, jdbcType=DECIMAL}
			,#{faclCd, jdbcType=DECIMAL}
			,#{faclImgType, jdbcType=VARCHAR}
			,#{mainImgYn, jdbcType=CHAR}
			,#{imgUrl, jdbcType=VARCHAR}
			,#{imgDesc, jdbcType=VARCHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
			,#{partnerImgUrl, jdbcType=VARCHAR}
		)
