mapper.insert:
INSERT INTO EZC_FACL(	/* 시설 정보 */
	 FACL_CD	/* 시설 코드, NUMBER(0,22) */
	,PARTNER_CD	/* 제휴사 코드, VARCHAR2(20) */
	,FACL_DIV	/* 시설 구분, VARCHAR2(8) */
	,PARTNER_GOODS_CD	/* 제휴사 상품 코드, VARCHAR2(100) */
	,FACL_NM_KOR	/* 시설 명 한글, VARCHAR2(100) */
	,FACL_NM_ENG	/* 시설 명 영문, VARCHAR2(100) */
	,ROOM_TYPE	/* 숙소 유형, VARCHAR2(8) */
