	,SALE_STOP_DT	/* 판매 중지 일시, VARCHAR2(14) */
	,API_SYNC_DT	/* API 동기화 일시, VARCHAR2(14) */
	,CONFIRM_STATUS	/* 확정 상태, VARCHAR2(8) */
	,FACL_STATUS	/* 시설 상태, VARCHAR2(8) */
	,REJECT_CONTENT	/* 반려 내용, VARCHAR2(1000) */
	,USE_YN	/* 사용 여부, CHAR(1) */
	,FACL_KOR_MORP /* 시설 한글 형태소 */
	,FACL_ENG_MORP /* 시설 영문 형태소 */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{faclCd, jdbcType=DECIMAL}
