mapper.update:
UPDATE EZC_FACL SET 
    USE_YN = #{useYn, jdbcType=VARCHAR}
WHERE FACL_DIV = #{faclDiv, jdbcType=VARCHAR}
	AND ( 	
		(REG_DT IS NOT NULL AND MODI_DT IS NULL AND TO_DATE(REG_DT, 'yyyyMMddHH24miss') < TO_DATE(#{regDt, jdbcType=VARCHAR}, 'yyyyMMddHH24miss'))
    	OR  
    	(REG_DT IS NOT NULL AND MODI_DT IS NOT NULL AND TO_DATE(MODI_DT, 'yyyyMMddHH24miss') < TO_DATE(#{modiDt, jdbcType=VARCHAR}, 'yyyyMMddHH24miss')) 
    )

MERGE INTO EZC_FACL /* 시설 정보 */
	USING DUAL ON (
		FACL_CD = #{faclCd, jdbcType=DECIMAL}	/* 시설 코드, NUMBER(0,22) */
		AND PARTNER_CD = #{partnerCd, jdbcType=VARCHAR}	/* 제휴사 코드, VARCHAR2(20) */
		AND AREA_CD = #{areaCd, jdbcType=VARCHAR}	/* 지역 코드, VARCHAR2(10) */
		AND CITY_CD = #{cityCd, jdbcType=VARCHAR}	/* 도시 코드, VARCHAR2(10) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 FACL_DIV = #{faclDiv, jdbcType=VARCHAR}	/* 시설 구분, VARCHAR2(8) */
			,PARTNER_GOODS_CD = #{partnerGoodsCd, jdbcType=VARCHAR}	/* 제휴사 상품 코드, VARCHAR2(100) */
			,FACL_NM_KOR = #{faclNmKor, jdbcType=VARCHAR}	/* 시설 명 한글, VARCHAR2(100) */
			,FACL_NM_ENG = #{faclNmEng, jdbcType=VARCHAR}	/* 시설 명 영문, VARCHAR2(100) */
			,ROOM_TYPE = #{roomType, jdbcType=VARCHAR}	/* 숙소 유형, VARCHAR2(8) */
			,ROOM_CLASS = #{roomClass, jdbcType=VARCHAR}	/* 숙소 등급, VARCHAR2(8) */
			,SALE_START_DD = #{saleStartDd, jdbcType=VARCHAR}	/* 판매 시작 일자, VARCHAR2(8) */
			,SALE_END_DD = #{saleEndDd, jdbcType=VARCHAR}	/* 판매 종료 일자, VARCHAR2(8) */
			,CHECK_IN_TM = #{checkInTm, jdbcType=VARCHAR}	/* 체크인 시분, VARCHAR2(4) */
			,CHECK_OUT_TM = #{checkOutTm, jdbcType=VARCHAR}	/* 체크아웃 시분, VARCHAR2(4) */
			,ADDR_TYPE = #{addrType, jdbcType=VARCHAR}	/* 주소 유형, VARCHAR2(8) */
			,ADDR = #{addr, jdbcType=VARCHAR}	/* 주소, VARCHAR2(200) */
			,POST = #{post, jdbcType=VARCHAR}	/* 우편번호, VARCHAR2(7) */
			,TEL_NUM = #{telNum, jdbcType=VARCHAR}	/* 전화 번호, VARCHAR2(20) */
			,COORD_Y = #{coordY, jdbcType=VARCHAR}	/* 위도, VARCHAR2(20) */
			,COORD_X = #{coordX, jdbcType=VARCHAR}	/* 경도, VARCHAR2(20) */
			,MIN_AMT = #{minAmt, jdbcType=NUMERIC}	/* 최저 금액, NUMBER(0,22) */
			,DETAIL_DESC_PC = #{detailDescPc, jdbcType=VARCHAR}	/* 상세 설명 PC, VARCHAR2(4000) */
			,DETAIL_DESC_M = #{detailDescM, jdbcType=VARCHAR}	/* 상세 설명 모바일, VARCHAR2(4000) */
			,TRIP_PROP_ID = #{tripPropId, jdbcType=VARCHAR}	/* 트립어드바이저 프로퍼티 ID, VARCHAR2(100) */
			,MAIN_IMG_URL = #{mainImgUrl, jdbcType=VARCHAR}	/* 대표 이미지 URL, VARCHAR2(200) */
			,IMG_CHANGE_YN = #{imgChangeYn, jdbcType=CHAR}	/* 이미지 변경 여부, CHAR(1) */
			,SALE_STOP_DT = #{saleStopDt, jdbcType=VARCHAR}	/* 판매 중지 일시, VARCHAR2(14) */
			,API_SYNC_DT = #{apiSyncDt, jdbcType=VARCHAR}	/* API 동기화 일시, VARCHAR2(14) */
			,CONFIRM_STATUS = #{confirmStatus, jdbcType=VARCHAR}	/* 확정 상태, VARCHAR2(8) */
			,FACL_STATUS = #{faclStatus, jdbcType=VARCHAR}	/* 시설 상태, VARCHAR2(8) */
			,REJECT_CONTENT = #{rejectContent, jdbcType=VARCHAR}	/* 반려 내용, VARCHAR2(1000) */
			,USE_YN = #{useYn, jdbcType=CHAR}	/* 사용 여부, CHAR(1) */
			,FACL_KOR_MORP = #{faclKorMorp, jdbcType=VARCHAR}	/* 시설 한글 형태소 */
			,FACL_ENG_MORP = #{faclEngMorp, jdbcType=VARCHAR}	/* 시설 영문 형태소 */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 FACL_CD	/* 시설 코드, NUMBER(0,22) */
			,PARTNER_CD	/* 제휴사 코드, VARCHAR2(20) */
