			,ROOM_TYPE	/* 숙소 유형, VARCHAR2(8) */
			,ROOM_CLASS	/* 숙소 등급, VARCHAR2(8) */
			,SALE_START_DD	/* 판매 시작 일자, VARCHAR2(8) */
			,SALE_END_DD	/* 판매 종료 일자, VARCHAR2(8) */
			,CHECK_IN_TM	/* 체크인 시분, VARCHAR2(4) */
			,CHECK_OUT_TM	/* 체크아웃 시분, VARCHAR2(4) */
