mapper.insert:
INSERT INTO EZC_FACL_REVIEW_GRADE(	/* 시설 후기 평점 */
	 FACL_REVIEW_TYPE	/* 시설 후기 유형, VARCHAR2(8) */
	,FACL_REVIEW_SEQ	/* 시설 후기 일련번호, NUMBER(0,22) */
	,GRADE	/* 평점, NUMBER(0,22) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{faclReviewType, jdbcType=VARCHAR}
	,#{faclReviewSeq, jdbcType=DECIMAL}
	,#{grade, jdbcType=NUMERIC}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_FACL_REVIEW_GRADE /* 시설 후기 평점 */
	USING DUAL ON (
		FACL_REVIEW_TYPE = #{faclReviewType, jdbcType=VARCHAR}	/* 시설 후기 유형, VARCHAR2(8) */
		AND FACL_REVIEW_SEQ = #{faclReviewSeq, jdbcType=DECIMAL}	/* 시설 후기 일련번호, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 GRADE = #{grade, jdbcType=NUMERIC}	/* 평점, NUMBER(0,22) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 FACL_REVIEW_TYPE	/* 시설 후기 유형, VARCHAR2(8) */
			,FACL_REVIEW_SEQ	/* 시설 후기 일련번호, NUMBER(0,22) */
			,GRADE	/* 평점, NUMBER(0,22) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{faclReviewType, jdbcType=VARCHAR}
			,#{faclReviewSeq, jdbcType=DECIMAL}
			,#{grade, jdbcType=NUMERIC}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
