mapper.insert:
INSERT INTO EZC_FACL_REVIEW(	/* 시설 후기 */
	 FACL_REVIEW_SEQ	/* 시설 후기 일련번호, NUMBER(0,22) */
	,RESERV_NUM	/* 예약 번호, NUMBER(0,22) */
	,REVIEW_CONTENT	/* 후기 내용, VARCHAR2(1000) */
	,IMG_YN	/* 이미지 여부, CHAR(1) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{faclReviewSeq, jdbcType=DECIMAL}
	,#{reservNum, jdbcType=DECIMAL}
	,#{reviewContent, jdbcType=VARCHAR}
	,#{imgYn, jdbcType=CHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_FACL_REVIEW /* 시설 후기 */
	USING DUAL ON (
		FACL_REVIEW_SEQ = #{faclReviewSeq, jdbcType=DECIMAL}	/* 시설 후기 일련번호, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 RESERV_NUM = #{reservNum, jdbcType=DECIMAL}	/* 예약 번호, NUMBER(0,22) */
			,REVIEW_CONTENT = #{reviewContent, jdbcType=VARCHAR}	/* 후기 내용, VARCHAR2(1000) */
			,IMG_YN = #{imgYn, jdbcType=CHAR}	/* 이미지 여부, CHAR(1) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 FACL_REVIEW_SEQ	/* 시설 후기 일련번호, NUMBER(0,22) */
			,RESERV_NUM	/* 예약 번호, NUMBER(0,22) */
			,REVIEW_CONTENT	/* 후기 내용, VARCHAR2(1000) */
			,IMG_YN	/* 이미지 여부, CHAR(1) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{faclReviewSeq, jdbcType=DECIMAL}
			,#{reservNum, jdbcType=DECIMAL}
			,#{reviewContent, jdbcType=VARCHAR}
			,#{imgYn, jdbcType=CHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
