mapper.insert:
INSERT INTO EZC_FREQ_TXT(	/* 상용구 관리 */
	 FREQ_TXT_CD	/* 상용구 코드, NUMBER(0,22) */
	,FREQ_TXT_CLASS	/* 상용구 분류, VARCHAR2(8) */
	,FREQ_TXT	/* 상용구, VARCHAR2(1000) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{freqTxtCd, jdbcType=DECIMAL}
	,#{freqTxtClass, jdbcType=VARCHAR}
	,#{freqTxt, jdbcType=VARCHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_FREQ_TXT /* 상용구 관리 */
	USING DUAL ON (
		FREQ_TXT_CD = #{freqTxtCd, jdbcType=DECIMAL}	/* 상용구 코드, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 FREQ_TXT_CLASS = #{freqTxtClass, jdbcType=VARCHAR}	/* 상용구 분류, VARCHAR2(8) */
			,FREQ_TXT = #{freqTxt, jdbcType=VARCHAR}	/* 상용구, VARCHAR2(1000) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 FREQ_TXT_CD	/* 상용구 코드, NUMBER(0,22) */
			,FREQ_TXT_CLASS	/* 상용구 분류, VARCHAR2(8) */
			,FREQ_TXT	/* 상용구, VARCHAR2(1000) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{freqTxtCd, jdbcType=DECIMAL}
			,#{freqTxtClass, jdbcType=VARCHAR}
			,#{freqTxt, jdbcType=VARCHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
