mapper.insert:
INSERT INTO EZC_HOLIDAY(	/* 공휴일 정보 */
	 HDAY_SEQ	/* 공휴일 일련번호, NUMBER(0,22) */
	,HDAY_TYPE_GB	/* 공휴일 유형 GB, VARCHAR2(8) */
	,SHOLIDAY_YY	/* 대체휴일 년도, VARCHAR2(4) */
	,HDAY_MMDD	/* 공휴일 월일, VARCHAR2(4) */
	,LUNAR_YN	/* 음력 여부, CHAR(1) */
	,HDAY_NM	/* 공휴일 명, VARCHAR2(50) */
	,HDAY_DESC	/* 공휴일 설명, VARCHAR2(1000) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
) VALUES (
	 #{hdaySeq, jdbcType=DECIMAL}
	,#{hdayTypeGb, jdbcType=VARCHAR}
	,#{sholidayYy, jdbcType=VARCHAR}
	,#{hdayMmdd, jdbcType=VARCHAR}
	,#{lunarYn, jdbcType=CHAR}
	,#{hdayNm, jdbcType=VARCHAR}
	,#{hdayDesc, jdbcType=VARCHAR}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_HOLIDAY /* 공휴일 정보 */
	USING DUAL ON (
		HDAY_SEQ = #{hdaySeq, jdbcType=DECIMAL}	/* 공휴일 일련번호, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 HDAY_TYPE_GB = #{hdayTypeGb, jdbcType=VARCHAR}	/* 공휴일 유형 GB, VARCHAR2(8) */
			,SHOLIDAY_YY = #{sholidayYy, jdbcType=VARCHAR}	/* 대체휴일 년도, VARCHAR2(4) */
			,HDAY_MMDD = #{hdayMmdd, jdbcType=VARCHAR}	/* 공휴일 월일, VARCHAR2(4) */
			,LUNAR_YN = #{lunarYn, jdbcType=CHAR}	/* 음력 여부, CHAR(1) */
			,HDAY_NM = #{hdayNm, jdbcType=VARCHAR}	/* 공휴일 명, VARCHAR2(50) */
			,HDAY_DESC = #{hdayDesc, jdbcType=VARCHAR}	/* 공휴일 설명, VARCHAR2(1000) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODI_DT = #{modiDt, jdbcType=VARCHAR}	/* 수정 일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 HDAY_SEQ	/* 공휴일 일련번호, NUMBER(0,22) */
			,HDAY_TYPE_GB	/* 공휴일 유형 GB, VARCHAR2(8) */
			,SHOLIDAY_YY	/* 대체휴일 년도, VARCHAR2(4) */
			,HDAY_MMDD	/* 공휴일 월일, VARCHAR2(4) */
			,LUNAR_YN	/* 음력 여부, CHAR(1) */
			,HDAY_NM	/* 공휴일 명, VARCHAR2(50) */
			,HDAY_DESC	/* 공휴일 설명, VARCHAR2(1000) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
		) VALUES (
			 #{hdaySeq, jdbcType=DECIMAL}
			,#{hdayTypeGb, jdbcType=VARCHAR}
			,#{sholidayYy, jdbcType=VARCHAR}
			,#{hdayMmdd, jdbcType=VARCHAR}
			,#{lunarYn, jdbcType=CHAR}
			,#{hdayNm, jdbcType=VARCHAR}
			,#{hdayDesc, jdbcType=VARCHAR}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
		)
