mapper.update:
MERGE INTO EZC_IF_LOG /* EZC 인터페이스 로그 */
	USING DUAL ON (
		IF_EXEC_CD = #{ifExecCd, jdbcType=VARCHAR}	/* 인터페이스 실행 코드, VARCHAR2(32) */
