mapper.insert:
INSERT INTO EZC_MAPPING_FACL(	/* 맵핑 시설 */
	 GRP_FACL_CD	/* 그룹 시설 코드, NUMBER(0,22) */
	,FACL_CD	/* 시설 코드, NUMBER(0,22) */
	,HAND_MAPPING_YN	/* 수동 맵핑 여부, CHAR(1) */
	,DISP_ORDER	/* 정렬 순서, NUMBER(0,22) */
	,REG_ID	/* 등록자 ID, VARCHAR2(20) */
	,REG_DT	/* 등록 일시, VARCHAR2(14) */
	,MODIDT	/* 수정일시, VARCHAR2(14) */
) VALUES (
	 #{grpFaclCd, jdbcType=DECIMAL}
	,#{faclCd, jdbcType=DECIMAL}
	,#{handMappingYn, jdbcType=CHAR}
	,#{dispOrder, jdbcType=NUMERIC}
	,#{regId, jdbcType=VARCHAR}
	,#{regDt, jdbcType=VARCHAR}
	,#{modidt, jdbcType=VARCHAR}
)

mapper.update:
MERGE INTO EZC_MAPPING_FACL /* 맵핑 시설 */
	USING DUAL ON (
		GRP_FACL_CD = #{grpFaclCd, jdbcType=DECIMAL}	/* 그룹 시설 코드, NUMBER(0,22) */
		AND FACL_CD = #{faclCd, jdbcType=DECIMAL}	/* 시설 코드, NUMBER(0,22) */
	)
	WHEN MATCHED THEN
		UPDATE SET
			 HAND_MAPPING_YN = #{handMappingYn, jdbcType=CHAR}	/* 수동 맵핑 여부, CHAR(1) */
			,DISP_ORDER = #{dispOrder, jdbcType=NUMERIC}	/* 정렬 순서, NUMBER(0,22) */
			,MODI_ID = #{modiId, jdbcType=VARCHAR}	/* 수정자 ID, VARCHAR2(20) */
			,MODIDT = #{modidt, jdbcType=VARCHAR}	/* 수정일시, VARCHAR2(14) */
	WHEN NOT MATCHED THEN
		INSERT (
			 GRP_FACL_CD	/* 그룹 시설 코드, NUMBER(0,22) */
			,FACL_CD	/* 시설 코드, NUMBER(0,22) */
			,HAND_MAPPING_YN	/* 수동 맵핑 여부, CHAR(1) */
			,DISP_ORDER	/* 정렬 순서, NUMBER(0,22) */
			,REG_ID	/* 등록자 ID, VARCHAR2(20) */
			,REG_DT	/* 등록 일시, VARCHAR2(14) */
			,MODIDT	/* 수정일시, VARCHAR2(14) */
		) VALUES (
			 #{grpFaclCd, jdbcType=DECIMAL}
			,#{faclCd, jdbcType=DECIMAL}
			,#{handMappingYn, jdbcType=CHAR}
			,#{dispOrder, jdbcType=NUMERIC}
			,#{regId, jdbcType=VARCHAR}
			,#{regDt, jdbcType=VARCHAR}
			,#{modidt, jdbcType=VARCHAR}
		)
